import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from 'src/components/CustomModal';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [modal, setModal] = React.useState(false)
  const { user,setLoad } = React.useContext(userContext);
  const [total, setTotal] = React.useState(0);
  const [userList, setUserList] = React.useState([]);

  React.useEffect(() => {
    setLoad(true)
    async function fetchData() {
      const response = await fetch(url + 'fetchUserList', {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })

      if (response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setUserList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'email': item.email,
              'role': item.role===2? "Admin" : "Accountant",
              'company_name': item.company_name
            }
          }))
          setTotal(parseInt(data.total_records));
        }
      }
    }
    fetchData();
  }, [])

  const deleteEntry = () => {
    setLoad(true)
    async function deleteData() {
      const response = await fetch(url + 'deleteuser/' + id, {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })

      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {

        setModal(false);
        setId('');
        async function fetchData() {
          const response = await fetch(url + 'fetchUserList', {
            method: 'GET',
            headers: {
              'Authorization': user?.token
            }
          })

          if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
              setUserList(data.list.map((item, index) => {
                return {
                  '#': index + 1,
                  'id': item.id,
                  'name': item.name,
                  'email': item.email,
                  'role': item.role===2? "Admin" : "Accountant",
                  'company_name': item.company_name
                }
              }))
              setTotal(parseInt(data.total_records));
            }
          }
        }
        fetchData();

      } else {
        toast.error(data.message)
      }
    }
    deleteData();
  }

  const columns = [
    {
        key: "#",
        text: "#",
        className: "#",
        sortable: false,
    },
    {
        key: "name",
        text: "User Name",
        className: "name",
        sortable: true
    },
    {
        key: "email",
        text: "Email",
        className: "email",
        sortable: true
    },
    {
      key: "company_name",
      text: "Company Name",
      className: "company_name",
      sortable: true
    },
    {
        key: "role",
        text: "Role",
        className: "role",
        sortable: true
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        sortable: false,
        cell: record => { 
            return (
                <Fragment>
                    <Link
                        to={`/edit/user/${record.id}`} 
                        style={{marginRight: '5px'}}>    
                        <i className="fa fa-pencil mr-1"></i>
                    </Link>
                    <i 
                    style={{ cursor: "pointer" }} 
                    onClick={() => showModal(record.id)} 
                    class="fa fa-trash" 
                    aria-hidden="true">   
                    </i>
                </Fragment>
            );
        }
    }
];
const config = {
  page_size: 10,
  length_menu: [ 10, 20, 50 ],
  button: {
      excel: false,
      print: false,
      extra: false,
  }
}
const showModal = value => {
  setId(value);
  setModal(true)
}

async function fetchData(query) {
  setLoad(true);
  const response = await fetch(url + query, {
      method: 'GET',
      headers: {
          'Authorization': user?.token
      }
  })
  if (response.ok === true) {
      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {
        setUserList(data.list.map((item, index) => {
          return {
            '#': index + 1,
            'id': item.id,
            'name': item.name,
            'email': item.email,
            'role': item.role===2? "Admin" : "Accountant",
            'company_name': item.company_name
          }
        }))
        setTotal(parseInt(data.total_records));
      } else {
          toast.error(data.message);
      }
  }
}
const handleChange=(e)=>{
let query=`fetchUserList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}`;
if(e.sort_order){
    let sort = JSON.stringify(e.sort_order);
    query=`fetchUserList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}`;
}
fetchData(query);
}

return (

    <section>
      <ToastContainer />
      <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
      <Link className='btn btn-primary float-right' to='/create/customer'>Create Customer</Link>
      <ReactDatatable
          config={config}
          records={userList}
          columns={columns}
          total_record={total}
          onChange={(e)=>handleChange(e)}
          dynamic={true}
        />
    </section>
  )
}
