export default function DetailCustomerReport() {

    const { user, setLoad } = React.useContext(userContext);
    const [customerDetailList, setCustomerDetailList] = React.useState([]);
    const [from_Date, setFromDate] = React.useState(new Date());
    const [to_Date, setToDate] = React.useState(new Date());
    const [currentCustomer, setCurrentCustomer] = React.useState('');
    const [totalAmount, settotalAmount] = React.useState(0);
    const [dueAmount, setDueAmount] = React.useState(0);
    const [paymentlist, setPaymentList] = React.useState([]);
    const [allCustomers, setAllCustomers] = React.useState([]);
    let index = 0;
    let index1 = 0;
    React.useEffect(() => {
        setLoad(true)
        async function fetchCustomers() {
            const response = await fetch(url + 'clientlist', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setAllCustomers(data?.client_list.map(item => {
                        return {
                            value: item.id,
                            label: item.name,
                            normal: item.normal,
                            star: item.star,
                            vinyl: item.vinyl,
                            one_way: item.one_way,
                            backlit: item.backlit,
                            premium: item.premium,
                            card12_18: item.card_12_18,
                            sticker12_18: item.sticker_12_18,
                            due_amount: item.due_amount
                        }
                    }))
                }
                else {
                    toast.error(data.message)
                }
            }
        }
        fetchCustomers();
    }, [])

    function handleSubmit() {
        if (to_Date && from_Date && currentCustomer) {
            setLoad(true)
            const formData = new FormData();
            formData.append('to', to_Date);
            formData.append('from', from_Date);
            formData.append('customer', JSON.stringify(currentCustomer));
            async function submit() {
                const response = await fetch(url + 'detailCustomerReport', {
                    method: 'POST',
                    headers: {
                        'Authorization': user?.token
                    },
                    body: formData
                });

                if (response.ok === true) {
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200) {
                        settotalAmount(parseFloat(data.total_amount).toFixed(2));
                        setCustomerDetailList(data.customer_detail);
                        setDueAmount(data.due_amount);
                        console.log(data.payment_list);
                        setPaymentList(data.payment_list);
                    } else {
                        toast.error(data.message)
                    }
                } else {
                    toast.error('Oops something went wrong!')
                }
            }

            submit().catch(err => toast.error('Internal server error!. Please try again later'))
        } else {
            toast.error('Please fill the to and from date')
        }
    }

    function exportPDF() {
        const html = document.getElementById('export-table').innerHTML;
        var myWindow = window.open('', 'Receipt');
        myWindow.document.write('<html><head><title>Receipt</title>');
        myWindow.document.write('<link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.1/dist/css/bootstrap.min.css" integrity="sha384-zCbKRCUGaJDkqS1kPbPd7TveP5iyJE0EjAuZQTgFLD2ylzuqKfdKlfG/eSrtxUkn" crossorigin="anonymous">')
        myWindow.document.write("</head><body>");
        myWindow.document.write(html);
        myWindow.document.write('</body></html>');
        myWindow.document.close();

        myWindow.onload = function () {
            myWindow.focus();
            myWindow.print();
        }
    }

    return (
        <section>
            <ToastContainer />
            <div class="d-flex">
                <form class="col-md-10 m-0 p-0">
                    <div class="form-row m-0 p-0 create-purchase-header">
                        <div class="form-group col-md-3">
                            <label for="date">From<span className='required-label'>*</span></label>
                            <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date" required />
                        </div>
                        <div class="form-group col-md-3">
                            <label for="date">To<span className='required-label'>*</span></label>
                            <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date" required />
                        </div>
                        <div class="form-group col-md-3">
                            <label for="input-customer">Customer<span className='required-label'>*</span></label>
                            <Select options={allCustomers} value={currentCustomer} onChange={setCurrentCustomer} />
                        </div>
                        <div class="form-group col-md-2">
                            <button type="button" class="font-weight-bold my-4 py-2 px-4 btn btn-success" onClick={() => handleSubmit()}>Submit</button>
                        </div>
                    </div>
                </form>
                <div class="col-md-2">
                    <button onClick={() => exportPDF()} class="font-weight-bold my-4 px-3 py-2 btn btn-success">Export as PDF</button>
                </div>
            </div>
            {/* table code */}
            <div id="export-table">
                <h6 className='text-center font-weight-bold'>{currentCustomer?.label}</h6>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Date</th>
                            <th scope="col">Item</th>
                            <th scope="col">Width</th>
                            <th scope="col">Height</th>
                            <th scope="col">Sq.Ft</th>
                            <th scope="col">Price</th>
                            <th scope="col">Qty</th>
                            <th scope="col">Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {customerDetailList.map((item) => {
                            return (
                                <tr>
                                    <th scope="row">{++index}</th>
                                    <td>{formatDate(item.sale_date.split(' ')[0])}</td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.item_name} ({item1.material_name})</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.width}</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.height}</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.sqFt}</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.sale_price}</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{item1.qty}</p>
                                            )
                                        })}
                                    </td>
                                    <td>
                                        {item.sale_items.map((item1) => {
                                            return (
                                                <p>{parseFloat(item1.total).toFixed(2)}</p>
                                            )
                                        })}
                                    </td>
                                </tr>
                            )
                        }
                        )}
                    </tbody>
                </table>
                {paymentlist.length > 0 && (
                    <>
                        <h6 className='text-center font-weight-bold'>Payment Recieved</h6>
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Reciept Number</th>
                                    <th scope="col">Date</th>
                                    <th scope="col">Amount</th>
                                </tr>
                            </thead>
                            <tbody>
                                {paymentlist.map((item) => {
                                    return (
                                        <tr>
                                            <th scope="row">{++index1}</th>
                                            <td>{item.id}</td>
                                            <td>{formatDate(item.date.split(' ')[0])}</td>
                                            <td>{item.amount}</td>
                                        </tr>
                                    )
                                }
                                )}
                            </tbody>
                        </table>
                    </>
                )}
                <hr />
                <h6 className='float-right mr-3'>Total Amount :- {parseFloat(totalAmount).toFixed(2)}</h6>
                <h6 className='float-right mr-3'>Due Amount :- {parseFloat(dueAmount).toFixed(2)}</h6>
            </div>
        </section >
    )
}
