import React, { Fragment } from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from '../../components/CustomModal';
import { formatDate } from 'src/helpers/helpers';
import PayModal from '../../components/PayModal';

export default function SalesReport() {
    const { user, setLoad } = React.useContext(userContext);
    const [salesList, setSalesList] = React.useState([]);
    const [modal, setModal] = React.useState(false);
    const [payAmount, setPayAmount] = React.useState(0);
    const [paymentMode, setPaymentMode] = React.useState({
        label: "cash"
    });
    const [paymodal, setPayModal] = React.useState(false);
    const [from_Date, setFromDate] = React.useState('');
    const [to_Date, setToDate] = React.useState('');
    const [id, setId] = React.useState('');
    const [total, setTotal] = React.useState(0);
    const [saleHeader, setSaleHeader] = React.useState("");
    const [saleItem, setSaleItem] = React.useState([]);
    const [customer, setCustomer] = React.useState("");

    const payBill = () => {
        setLoad(true)
        async function payBillServer() {
            const formData = new FormData();
            formData.append('pay_amount', payAmount);
            formData.append('payment_mode', paymentMode.label);
            const response = await fetch(url + 'pay/sales/' + id, {
                method: 'POST',
                headers: {
                    'Authorization': user.token
                },
                body: formData
            })

            if (response) {
                const data = await response.json();

                if (data.status === 200) {
                    setPayModal(false);
                    setId('');
                    async function fetchData() {
                        const response = await fetch(url + 'fetchSalesList', {
                            method: 'GET',
                            headers: {
                                'Authorization': user?.token
                            }
                        })
                        if (response.ok === true) {
                            const data = await response.json();
                            setLoad(false)
                            if (data.status === 200) {
                                setSalesList(data.list.map((item, index) => {
                                    return {
                                        '#': index + 1,
                                        'id': item.id,
                                        'invoice_number': item.invoice_number,
                                        'customer_name': item.customer_name,
                                        'warehouse_name': item.warehouse_name,
                                        'sale_date': formatDate(item.sale_date.split(' ')[0]),
                                        'total_after_roundoff': item.total_after_roundoff,
                                        'remain_amount': item.remain_amount
                                    }
                                }));
                                setTotal(parseInt(data.total_records));
                            } else {
                                toast.error(data.message);
                            }
                        }
                    }
                    fetchData();
                } else {
                    toast.error(data.message);
                }
            }
        }
        payBillServer();
    }

    const payModal = (item, id) => {
        setPayAmount(parseFloat(item.remain_amount))
        setPayModal(true);
        setId(id);
    }

    const deleteEntry = () => {
        setLoad(true)
        async function deleteData() {
            const response = await fetch(url + 'deleteSale/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            const data = await response.json();

            if (data.status === 200) {
                setModal(false);
                setId('');
                async function fetchData() {
                    const response = await fetch(url + 'fetchSalesList', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })

                    if (response.ok === true) {
                        const data = await response.json();
                        setLoad(false)
                        if (data.status === 200) {
                            setSalesList(data.list.map((item, index) => {
                                return {
                                    '#': index + 1,
                                    'id': item.id,
                                    'customer_name': item.customer_name,
                                    'warehouse_name': item.warehouse_name,
                                    'sale_date': formatDate(item.sale_date.split(' ')[0]),
