import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify';
import { userContext } from '../../context/UserContext';
import CustomModal from 'src/components/CustomModal';
import { formatDate } from 'src/helpers/helpers';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [modal, setModal] = React.useState(false)
  const { user,setLoad } = React.useContext(userContext);
  const [companyList, setCompanyList] = React.useState([]);
  const [total,setTotal] = React.useState(0); 

  React.useEffect(() => {
    setLoad(true)
    async function fetchData() {
      const response = await fetch(url + 'fetchCompanyList', {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })
      if(response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setCompanyList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'email': item.email,
              'mobile': item.mobile,
              'gst': item.gst,
              'default_gst': item.default_gst==="true"?"GST" : "NON-GST",
              'expiry_date': formatDate(item.expiry_date.split(' ')[0]),
              'print_preference_purchase': item.print_preference_purchase,
              'print_preference_sales': item.print_preference_sales,
              'one_time_amount': item.one_time_amount,
              'renew_amount': item.renew_amount,
              'status': item.status===1?"Active" : "Inactive"
            }
          }));
          setTotal(parseInt(data.total_records));
        }
      }
    }
    fetchData();
  }, [])

  const deleteEntry = () => {
    setLoad(true)
    async function deleteData() {
      const response = await fetch(url + 'deletecompany/' + id, {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })
      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {
        setModal(false);
        setId('');
        async function fetchData() {
          const response = await fetch(url + 'fetchCompanyList', {
            method: 'GET',
            headers: {
              'Authorization': user?.token
            }
          })

          if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                setCompanyList(data.list.map((item, index) => {
                    return {
                      '#': index + 1,
                      'id': item.id,
                      'name': item.name,
                      'email': item.email,
                      'mobile': item.mobile,
                      'gst': item.gst,
                      'default_gst': item.default_gst==="true" ? "GST" : "NON-GST",
                      'expiry_date': formatDate(item.expiry_date.split(' ')[0]),
                      'one_time_amount': item.one_time_amount,
                      'renew_amount': item.renew_amount,
                      'print_preference_purchase': item.print_preference_purchase,
                      'print_preference_sales': item.print_preference_sales,
                      'status': item.status===1?"Active" : "Inactive"
                    }
                  }));
              setTotal(parseInt(data.total_records));
            }
          }
        }
        fetchData();
      } else {
        toast.error(data.message)
      }
    }
    deleteData();
  }

  const columns = [
    {
        key: "#",
        text: "#",
        className: "#",
        sortable: false,
    },
    {
        key: "name",
        text: "Company Name",
        className: "name",
        sortable: true
    },
    {
        key: "email",
        text: "Email",
        className: "email",
        sortable: true
    },
    {
        key: "mobile",
        text: "Mobile",
        className: "mobile",
        sortable: true
    },
    {
      key: "default_gst",
      text: "Company Type(GST)",
      className: "default_gst",
      sortable: true
    },
    {
        key: "gst",
        text: "GST No.",
        className: "gst",
        sortable: true
    },
    {
      key: "print_preference_purchase",
      text: "Purchase Preference",
      className: "print_preference_purchase",
      sortable: true
    },
    {
        key: "print_preference_sales",
        text: "Sales Preference",
        className: "print_preference_sales",
        sortable: true
    },
    {
        key: "one_time_amount",
        text: "First Amount",
        className: "one_time_amount",
        sortable: true
    },
    {
        key: "renew_amount",
        text: "Renew amount",
        className: "renew_amount",
        sortable: true
    },
    {
        key: "status",
        text: "Status",
        className: "status",
        sortable: true
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        sortable: false,
        cell: record => { 
            return (
                <Fragment>      
                    <Link
                        to={`/edit/company/${record.id}`} 
                        style={{marginRight: '5px'}}>    
                        <i className="fa fa-pencil mr-1"></i>
                    </Link>
                    <i 
                    style={{ cursor: "pointer" }} 
                    onClick={() => showModal(record.id)} 
                    class="fa fa-trash" 
                    aria-hidden="true">   
                    </i>
                </Fragment>
            );
        }
    }
];

const config = {
    page_size: 10,
    length_menu: [ 10, 20, 50 ],
    button: {
        excel: false,
        print: false,
        extra: false,
    }
}
  const showModal = value => {
    setId(value);
    setModal(true)
  }
  async function fetchData(query) {
    setLoad(true);
    const response = await fetch(url + query, {
        method: 'GET',
        headers: {
            'Authorization': user?.token
        }
    })
    if (response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
            setCompanyList(data.list.map((item, index) => {
                return {
                  '#': index + 1,
                  'id': item.id,
                  'name': item.name,
                  'email': item.email,
                  'mobile': item.mobile,
                  'gst': item.gst,
                  'default_gst': item.default_gst==="true"?"GST" : "NON-GST",
                  'expiry_date': formatDate(item.expiry_date.split(' ')[0]),
                  'one_time_amount': item.one_time_amount,
                  'renew_amount': item.renew_amount,
                  'print_preference_purchase': item.print_preference_purchase,
                  'print_preference_sales': item.print_preference_sales,
                  'status': item.status===1?"Active" : "Inactive"
                }
              }));
          setTotal(parseInt(data.total_records));
        } else {
            toast.error(data.message);
        }
    }
}
const handleChange=(e)=>{
  let query=`fetchCompanyList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}`;
  if(e.sort_order){
      let sort = JSON.stringify(e.sort_order);
      query=`fetchCompanyList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}`;
  }
  fetchData(query);
}

  return (
    <section>
      <ToastContainer />
      <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
      <Link className='btn btn-primary float-right' to='/create/company'>Create Company</Link>
      <ReactDatatable
          config={config}
          records={companyList}
          columns={columns}
          total_record={total}
          onChange={(e)=>handleChange(e)}
          dynamic={true}
        />
    </section>
  )
}
