import React from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'

export default function Index() {
    const { user, setLoad } = React.useContext(userContext);
    const [historyList, setHistoryList] = React.useState([]);
    const [from_Date, setFromDate] = React.useState('');
    const [to_Date, setToDate] = React.useState('');
    const [total,setTotal] = React.useState(0);
    const [purpose,setPurpose] = React.useState('');
    const [action, setAction] = React.useState('');

    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'fetchHistoryList', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setHistoryList(data.list.map((item,index)=>{
                        return{
                           '#' : index+1,
                           'id': item.id,
                           'action': item.action,
                           'date': item.date,
                           'purpose': item.purpose,
                           'user_name': item.user_name
                        }
                    }));
                    setTotal(parseInt(data.total_records));
                } else {
                    toast.error(data.message);
                }
            }
        }
        fetchData();
    }, [])

    function handleSubmit(){
        let string="";
        if(to_Date && from_Date && action && purpose){
           string=`to=${to_Date}&from=${from_Date}&purpose=${purpose}&action=${action}`;
        }
        else if(to_Date && from_Date){
            if(purpose){
            string=`to=${to_Date}&from=${from_Date}&purpose=${purpose}`;
            }
            else if(action){
                string=`to=${to_Date}&from=${from_Date}&action=${action}`;
            }
            else{
                string=`to=${to_Date}&from=${from_Date}`;
            }
        }
        else if(purpose){
            if(action){
                string=`purpose=${purpose}&action=${action}`;
            }
            else string=`purpose=${purpose}`;
        }
        else string=`action=${action}`;
            setLoad(true)
            async function submit(){
                const response = await fetch(url + `fetchHistoryList?${string}`,{
                    method : 'GET',
                    headers : {
                        'Authorization' : user?.token
                    }
                });

                if (response.ok === true){
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200){
                        setHistoryList(data.list.map((item,index)=>{
                            return{
                               '#' : index+1,
                               'id': item.id,
                               'action': item.action,
                               'date': item.date,
                               'purpose': item.purpose,
                               'user_name': item.user_name
                            }
                        }));
                        setTotal(parseInt(data.total_records));
                    }else{
                        toast.error(data.message)
                    }
                }else{
                    toast.error('Oops something went wrong!')
                }
            }
            submit().catch(err=>toast.error('Internal server error!. Please try again later'))
    }

    const columns = [
        {
            key: "#",
            text: "#",
            className: "#",
            sortable: true,
        },
        {
            key: "action",
            text: "Action",
            className: "action",
            sortable: true,
        },
        {
            key: "date",
            text: "Date",
            className: "date",
            sortable: true
        },
        {
            key: "purpose",
            text: "Purpose",
            className: "purpose",
            sortable: true
        },
        {
            key: "user_name",
            text: "Created By",
            className: "user_name",
            sortable: true
        },
    ];
    const config = {
        page_size: 10,
        length_menu: [ 10, 20, 50 ],
        button: {
            excel: false,
            print: false,
            extra: false,
        }
    }
    async function fetchData(query) {
        setLoad(true);
        const response = await fetch(url + query, {
            method: 'GET',
            headers: {
                'Authorization': user?.token
            }
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                setHistoryList(data.list.map((item,index)=>{
                    return{
                       '#' : index+1,
                       'id': item.id,
                       'action': item.action,
                       'date': item.date,
                       'purpose': item.purpose,
                       'user_name': item.user_name
                    }
                }));
                setTotal(parseInt(data.total_records));
            } else {
                toast.error(data.message);
            }
        }
    }
    const handleChange=(e)=>{
        let query=`fetchHistoryList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&to=${to_Date}&from=${from_Date}`;
        if(e.sort_order){
            let sort = JSON.stringify(e.sort_order);
            query=`fetchHistoryList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}&to=${to_Date}&from=${from_Date}`;
        }
        fetchData(query);
    }

    return (
        <section>
            <ToastContainer />
            <form>
            <div class="form-row create-purchase-header">
            <div class="form-group col-md-3">
            <label for="date">From</label>
            <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date"/>
            </div>
            <div class="form-group col-md-3">
            <label for="date">To</label>
            <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date"/>
            </div>
            <div class="form-group col-md-3">
            <label for="input-type" class="col-md-4 col-form-label">Action</label>
            <select value={action} onChange={e => setAction(e.target.value)} id="input-type" class="form-control">
            <option value="">Select Action</option>
            <option value="Create">Create</option>
            <option value="Update">Update</option>
            <option value="Delete">Delete</option>
            </select>
            </div>
            <div class="form-group col-md-3">
            <label for="input-type" class="col-md-4 col-form-label">Purpose</label>
            <select value={purpose} onChange={e => setPurpose(e.target.value)} id="input-type" class="form-control">
            <option value="">Select Purpose</option>
            <option value="Categories">Category</option>
            <option value="Customers">Customer</option>
            <option value="Employees">Employee</option>
            <option value="Expenses">Expense</option>
            <option value="Inventories">Inventory</option>
            <option value="Parties">Party</option>
            <option value="Purchase_Start">Purchase</option>
            <option value="Purchase_Return">Purchase Return</option>
            <option value="Sale_Start">Sales</option>
            <option value="Sale_Return">Sales Return</option>
            <option value="Quotation">Quotation</option>
            <option value="Reciepts">Reciept</option>
            </select>
            </div>
            <div class="form-group col-md-3">
            <button type="Submit" class="font-weight-bold my-4 py-2 px-4 btn btn-success" onClick={ ()=> handleSubmit() }>Filter</button>
            </div>
            </div>
            </form>
            <ReactDatatable
                    config={config}
                    records={historyList}
                    columns={columns}
                    total_record={total}
                    onChange={(e)=>handleChange(e)}
                    dynamic={true}
                />
        </section>
    )
}
