import React from 'react'
import plus from '../../../assets/images_manual/add.svg'
import Select from 'react-select';
import { url } from 'src/helpers/helpers';
import { userContext } from '../../../context/UserContext'
import { toast, ToastContainer } from 'react-toastify';
import CustomerModal from '../../../components/CreateCustomerModal';

export default function Create() {
    const { user,setLoad } = React.useContext(userContext);
    const [invoiceNo, setInvoiceNo] = React.useState('');
    const [saleDate, setSaleDate] = React.useState('');
    const [default_gst, setDefaultGST] = React.useState(false);
    const [currentCustomer, setCurrentCustomer] = React.useState('');
    const [customermodal, setCustomerModal] = React.useState(false);
    const [currentWarehouse, setCurrentWarehouse] = React.useState('');
    const [discountType, setDiscountType] = React.useState('None');
    const [discountValue, setDiscountValue] = React.useState(0);
    const [currentDiscountCriteria, setCurrentDiscountCriteria] = React.useState('percent');
    const [currentPaymentMethod, setCurrentPaymentMethod] = React.useState('');
    const [currentProduct, setCurrentProduct] = React.useState('');
    const [finalDiscountValue, setFinalDiscountValue] = React.useState(0);
    const [finalDiscountCriteria, setFinalDiscountCriteria] = React.useState('percent');
    const [sale_price, setSalePrice] = React.useState(0);
    const [gst,setGST] = React.useState({
        value: 0,
        label: 0
    });
    const [qty, setQty] = React.useState(1);
    const [gst_list,setGSTList] = React.useState([]);
    const [remarks, setRemarks] = React.useState('');

    const [allCustomers, setAllCustomers] = React.useState([]);
    const [allWarehouses, setAllWarehouses] = React.useState([]);
    const [allPaymentMethods,setAllPaymentMethods] = React.useState([]);
    const [allItems, setAllItems] = React.useState([]);
    const [allProducts,setAllProducts] = React.useState([]);

    //final related variables and states
    let finalAmount = 0;
    let remainAmount = 0;
    let discount_amount_final = 0;
    let amount_before_discount_final = 0;
    let amount_after_discount_final = 0;

    const [paidAmount, setPaidAmount] = React.useState(0);
    const [totalValue, setTotalValue] = React.useState(0);
    const [discountAmount,setDiscountAmount] = React.useState(0);
    const [taxAmount,setTaxableAmount] = React.useState(0);

    //edit related states
    const [currentEditItem, setCurrentEditItem] = React.useState('');

    //item based variables
    let perItemAmount = 0;
    let tax_amount = 0;
    let amount_item = 0;
    let amount_before_discount = 0;
    let amount_after_discount = 0;
    let amount_after_tax=0;
    let discount_amount = 0;

    React.useEffect(() => {

        async function fetchPaymentMethods() {
            setLoad(true);
            const response = await fetch(url + 'getPaymentMethods', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            const data = await response.json();
            setLoad(false);
            setAllPaymentMethods(data?.payment_methods.map(item => {
                return {
                    label: item
                }
            }))
            setCurrentPaymentMethod({
                label: "cash"
            });
        }
        fetchPaymentMethods();

        async function fetchData() {
            setLoad(true);
            const response = await fetch(url + 'clientlist', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setAllCustomers(data?.client_list.map(item => {
                        return {
                            value: item.id,
                            label: item.name,
                            amount: item.due_amount
                        }
                    }))
                } else {
                    toast.error(data.message)
                }
            }
        }
        fetchData();

        async function fetchGSTList() {
            setLoad(true);
            const response = await fetch(url + 'getGSTList', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            const data = await response.json();
            setLoad(false);
            setGSTList(data?.gst.map(item => {
                return {
                    value: item,
                    label: item
                }
            }))
        }
        fetchGSTList();

        async function fetchWarehouse() {
            setLoad(true);
            const response = await fetch(url + 'warehouselist', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setAllWarehouses(data?.warehouse_list.map(item => {
                        return {
                            value: item.id,
                            label: item.name
                        }
                    }))
                } else {
                    toast.error(data.message)
                }
            }
        }
        fetchWarehouse();
        fetchDefaultGST();
    }, [])

    async function fetchDefaultGST(){
        setLoad(true);
        const response = await fetch(url + `fetchGST`,{
            headers : {
                'Authorization' : user.token
            },
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false);
            if (data.status == 200) {  
                setDefaultGST(data.default_gst);
                setInvoiceNo(data.invoice_number);
                setGST({
                   value: 0,
                   label: 0
                });
            }else{
                toast.error(data.message);
            }
        }        
    }

    async function fetchProd(e) {
        setLoad(true);            
        const response = await fetch(url + `fetchProducts?warehouse=${e.value}`, {
            method: 'GET',
            headers: {
                'Authorization': user?.token
            }
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                let products=[];
                if(data.inventory_list){
                  data.inventory_list.map(item=>{
                    products.push(
                       {value : item.product_id,
                        name : item.product_name,
                        label : `${item.product_name} ${item.total_qty}`,
                        type : "product",
                        unit : item.product_unit,
                        price: item.sale_unit_price,
                        hsbn: item.product_hsbn,
                        serial_no: item.product_serial_no
                       }
                  )})
                }
                if(data.product_list){
                    data.product_list.map(item=>{
                      products.push(
                         {value : item.id,
                          name: item.name,  
                          label : item.name,
                          type: item.type,
                          price: item.price,
                          unit: item.unit,
                          hsbn: item.hsbn,
                          serial_no: item.serial_no
                         }
                    )})
                  }  
                setAllProducts(products);
            } else {
                toast.error(data.message)
            }
        }
    }
    const showModal = () => {
        setCustomerModal(true)
    }
    const calculateTotal = () => {
        if (sale_price > 0 && qty > 0) {
            amount_item = parseFloat(sale_price) * parseFloat(qty); //amount before tax
            tax_amount=0;
            if(default_gst && gst){
            tax_amount = (parseFloat(amount_item)/100) * parseFloat(gst.value);
            }
            amount_after_tax = amount_item + tax_amount;
            perItemAmount = amount_after_tax;

            if (discountType === 'Item' || discountType === 'Both') {
                amount_before_discount = amount_after_tax;
                if (currentDiscountCriteria === 'percent') {
                    discount_amount = (parseFloat(amount_before_discount) / 100) * parseFloat(discountValue)
                    amount_after_discount = amount_before_discount - discount_amount;
                    perItemAmount = amount_after_discount;
                }
                if (currentDiscountCriteria === 'amount') {
                    discount_amount = parseFloat(discountValue);
                    amount_after_discount = amount_before_discount - discount_amount;
                    perItemAmount = amount_after_discount;
                }
            }
        }
    }
    calculateTotal()

    const calculateFinalPrice = () => {
        if (totalValue > 0) {
            if (discountType === 'Bill' || discountType === 'Both') {
                if (finalDiscountCriteria === 'percent') {
                    discount_amount_final = totalValue / 100 * parseFloat(finalDiscountValue);
                    amount_before_discount_final = totalValue;
                    amount_after_discount_final = totalValue - discount_amount_final;
                }

                if (finalDiscountCriteria === 'amount') {
                    discount_amount_final = parseFloat(finalDiscountValue);
                    amount_before_discount_final = totalValue;
                    amount_after_discount_final = totalValue - discount_amount_final;
                }
                finalAmount = amount_after_discount_final;
                remainAmount = finalAmount - parseFloat(paidAmount);
            } else {
                finalAmount = totalValue
                remainAmount = finalAmount - parseFloat(paidAmount);
            }
        }
    }
    calculateFinalPrice()

    const handleSubmitItem = () => {
        if (invoiceNo && currentProduct && currentWarehouse && currentCustomer && sale_price && qty) {
            let new_item_dict = {
                item: currentProduct,
                sale_price: sale_price,
                qty: qty,
                gst: gst,
                discount_type: currentDiscountCriteria,
                discountValue,
                total: perItemAmount,
                amount_item,
                amount_before_discount,
                discount_amount,
                tax_amount,
                amount_after_discount
            };
            if (currentEditItem) {
                setCurrentEditItem('')
                let new_list_edit = []
                allItems.map((item, index) => {
                    if (index === parseInt(currentEditItem)-1) {
                        new_list_edit.push(new_item_dict)
                    } else {
                        new_list_edit.push(item)
                    }
                })
                let total = 0;
                let discount_amount=0;
                let taxable_amount=0;
                new_list_edit.map(item=>{
                    total = parseFloat(total) + parseFloat(item.total);
                    discount_amount = parseFloat(discount_amount) + parseFloat(item.discount_amount);
                    taxable_amount = parseFloat(taxable_amount) + parseFloat(item.tax_amount);
                })
                setTotalValue(total);
                setDiscountAmount(discount_amount);
                setTaxableAmount(taxable_amount);
                setAllItems(new_list_edit);
                setPaidAmount(total)
            } else {
                let new_item_list = [...allItems, new_item_dict];
                let total = 0;
                let taxable_amount=0;
                new_item_list.map(item=>{
                    total = parseFloat(total) + parseFloat(item.total);
                    discount_amount = parseFloat(discount_amount) + parseFloat(item.discount_amount);
                    taxable_amount = parseFloat(taxable_amount) + parseFloat(item.tax_amount);
                })
                setDiscountAmount(discount_amount);
                setTaxableAmount(taxable_amount);
                setTotalValue(total);
                setPaidAmount(total)
                setTotalValue(parseFloat(totalValue) + parseFloat(perItemAmount))
                setAllItems(new_item_list);
            }
            setCurrentProduct('');
            setSalePrice(0);
            setCurrentDiscountCriteria('');
            setDiscountValue(0);
            setQty(1);
            setGST({
                value: 0,
                label: 0
            });
            perItemAmount = 0;
            amount_item= 0;
            amount_before_discount = 0;
            amount_after_discount = 0;
            discount_amount = 0;
            tax_amount=0;
            amount_after_tax=0;
        } else {
            toast.error('Fill all fields having *')
        }
    }
    const finalSubmit = () => {
        if (invoiceNo && currentWarehouse && currentCustomer && allItems.length > 0 && totalValue){
            setLoad(true)
            let header = {
                invoiceNo : invoiceNo,
                saleDate : saleDate,
                default_gst: default_gst,
                currentCustomer : currentCustomer,
                currentWarehouse : currentWarehouse,
                discountType : discountType
            }
            let final = {
                totalValue : totalValue,
                finalAmount : finalAmount,
                paidAmount : paidAmount,
                remainAmount : remainAmount,
                finalDiscountValue : finalDiscountValue,
                finalDiscountCriteria : finalDiscountCriteria,
                remarks : remarks,
                paymentMethod : currentPaymentMethod?.label,
                discountAmount : discountAmount,
                taxableAmount : taxAmount
            }

            let final_array = [];

            final_array.push(header);
            final_array.push(final);

            const formData = new FormData();
            formData.append('final_array',JSON.stringify(final_array));
            formData.append('allItems',JSON.stringify(allItems));

            async function submitSales(){
                const response = await fetch(url + 'createSales',{
                    method : 'POST',
                    headers : {
                        'Authorization' : user.token
                    },
                    body : formData
                });
                if (response.ok === true){
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200){
                        toast.success(data.message);
                        setSaleDate('');
                        setCurrentCustomer('');
                        setDiscountType('None');
                        setDiscountAmount(0);
                        setDefaultGST(false);
                        setTaxableAmount(0);
                        setFinalDiscountCriteria('percent');
                        setFinalDiscountValue(0);
                        setCurrentProduct('');
                        setCurrentWarehouse('');
                        setTotalValue(0);
                        setPaidAmount(0);
                        setAllItems([]);
                        setAllProducts([]);
                        setCurrentPaymentMethod({
                            label:"cash"
                        });
                        finalAmount = 0;
                        remainAmount = 0;
                        discount_amount_final = 0;
                        amount_before_discount_final = 0;
                        amount_after_discount_final = 0;
                        fetchDefaultGST();
                    }else{
                        toast.error(data.message);
                    }
                }
            }
            submitSales()
        }else{
            toast.error('Please fill the data with *');
        }
    }
    const handleEdit = (item, index_value) => {
        setCurrentEditItem(index_value);
        setCurrentProduct(item.item);
        setSalePrice(item.sale_price);
        setQty(item.qty);
        setGST(item.gst);
        setCurrentDiscountCriteria(item.discount_type);
        setDiscountValue(item.discountValue);
        perItemAmount = item.total;
    } 
    const handleDelete = (index_value) => {
        const new_item = [];
        allItems.map((item, index) => {
            if (index !== index_value) {
                new_item.push(item)
            }
        })
        setAllItems(new_item);
    }
    const handleChange = ()=>{
        const def_gst = !default_gst;
        setDefaultGST(!default_gst);
        async function fetchInvoiceNo(){
            setLoad(true);
            const response = await fetch(url + `fetchInvoiceNo?default_gst=${def_gst}`,{
                headers : {
                    'Authorization' : user.token
                },
            })
            if (response.ok === true) {
                const data = await response.json();
                setLoad(false);
                if (data.status == 200) {  
                    setInvoiceNo(data.invoice_number);
                }
            }        
        }
        fetchInvoiceNo();
    }
    const handleProducts = (e)=>{
        setCurrentWarehouse(e);
        fetchProd(e); 
    }

    const handleProduct = (e)=>{
        setCurrentProduct(e);
        setSalePrice(e.price);
    }

    return (
        <div className="container full-size-create-page-main-section">
            <ToastContainer />
            <CustomerModal customermodal={customermodal} setCustomerModal={setCustomerModal} allCustomers={allCustomers} setAllCustomers={setAllCustomers} setCurrentCustomer={setCurrentCustomer} /> 
            <form>
                <div className='p-sm-5 px-md-3 create-form-field create-purchase-page'>
                    <div class="py-4 px-2 form-row create-purchase-header">
                        <div class="form-group col-md-3">
                            <label for="invoice-number">Invoice No.<span className='required-label'>*</span></label>
                            <input readOnly value={invoiceNo} type="text" class="form-control" id="invoice-number" required />
                        </div>
                        <div class="form-group col-md-4">
                            <label for="input-party">Warehouse<span className='required-label'>*</span></label>
                            <Select options={allWarehouses} value={currentWarehouse} onChange={(e)=>handleProducts(e)} />
                        </div>
                        <div class="form-group col-md-4">
                            <label for="date">Sale Date</label>
                            <input value={saleDate} onChange={e => setSaleDate(e.target.value)} type="date" class="form-control" id="date"/>
                        </div>
                        <div class="form-group col-md-5">
                            <div class="form-row">
                                <div class="form-group col-md-10">
                                <label for="input-party">Customer<span className='required-label'>*</span></label>
                                <Select options={allCustomers} value={currentCustomer} onChange={setCurrentCustomer} />
                                </div>
                                <div class="form-group col-md-2 purchase-create-mid-plus-img">
                                <img className='img-fluid' onClick={() => showModal()} style={{ cursor: 'pointer' }} src={plus} alt="" />
                                </div>
                            </div> 
                        </div>
                        <div class="form-group my-md-3 col-md-4">
                            <div className="text-center mb-2 font-weight-bold">
                                <label for="input-party">Discount Type<span className='required-label'>*</span></label>
                            </div>
                            <div className='row px-3'>
                                <div class="form-check col-sm-3">
                                    <input checked={discountType === 'None' && true} onClick={() => setDiscountType('None')} class="form-check-input" name="discount" type="radio" value="" id="defaultCheck1" />
                                    <label class="form-check-label" for="defaultCheck1">
                                        None
                                    </label>
                                </div>
                                <div class="form-check col-sm-3">
                                    <input onClick={() => setDiscountType('Item')} class="form-check-input" name="discount" type="radio" value="" id="defaultCheck2" />
                                    <label class="form-check-label" for="defaultCheck2">
                                        Item
                                </label>
                                </div>
                                <div class="form-check col-sm-3">
                                    <input onClick={() => setDiscountType('Bill')} class="form-check-input" name="discount" type="radio" value="" id="defaultCheck3" />
                                    <label class="form-check-label" for="defaultCheck3">
                                        Bill
                                </label>
                                </div>
                                <div class="form-check col-sm-3">
                                    <input onClick={() => setDiscountType('Both')} class="form-check-input" name="discount" type="radio" value="" id="defaultCheck3" />
                                    <label class="form-check-label" for="defaultCheck3">
                                        Both
                                </label>
                                </div>
                            </div>
                        </div>
                        <div class="form-group col-md-2">
                        <label for="inputPassword" class="col-sm-2 col-form-label">GST:</label>
                        <div class="d-flex b-0 col-sm-1">
                        <input checked={default_gst} type="checkbox" onChange={e => handleChange()} id="inputPassword" />
                        </div>
                        </div>
                    </div>    
                    <div class="my-4 form-row">
                        <div class="form-group col-md-4">
                            <label for="invoice-number">Item<span className='required-label'>*</span></label>
                            <Select options={allProducts} value={currentProduct} onChange={(e)=>handleProduct(e)} />
                        </div>
                        <div class="form-group col-md-4">
                            <label for="invoice-number">Price<span className='required-label'>*</span></label>
                            <input value={sale_price} onChange={(e) => setSalePrice(e.target.value)} type="text" class="form-control" id="" />
                        </div>
                        <div class="form-group col-md-4">
                            <label for="invoice-number">Quantity{currentProduct ? ` (${currentProduct.unit})` :null}<span className='required-label'>*</span></label>
                            <input value={qty} onChange={e => setQty(e.target.value)} type="text" class="form-control" id="" />
                        </div>
                        {default_gst &&
                        <div class="form-group col-md-4">
                            <label for="invoice-number">GST:<span className='required-label'>*</span></label>
                            <Select options={gst_list} value={gst} onChange={setGST} />
                        </div>
                         }
                        {discountType === 'Item' &&
                            <div className='form-group col-md-4'>
                                <label htmlFor="">Discount</label>
                                <div className='m-0 p-0 col-12 row'>
                                    <div className="px-0 col-5">
                                        <input value={discountValue} onChange={e => setDiscountValue(e.target.value)} class="form-control" type="text" name="" id="" />
                                    </div>
                                    <div className="p-0 col-7">
                                        <select value={currentDiscountCriteria} onChange={e => setCurrentDiscountCriteria(e.target.value)} class="form-control" required>
                                            <option value="">Select Type</option>
                                            <option value='percent'>Percent</option>
                                            <option value='amount'>Amount</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        }
                        {discountType === 'Both' &&
                            <div className='form-group col-md-4'>
                                <label htmlFor="">Discount</label>
                                <div className='m-0 p-0 col-12 row'>
                                    <div className="px-0 col-5">
                                        <input value={discountValue} onChange={e => setDiscountValue(e.target.value)} class="form-control" type="text" name="" id="" />
                                    </div>
                                    <div className="p-0 col-7">
                                        <select value={currentDiscountCriteria} onChange={e => setCurrentDiscountCriteria(e.target.value)} class="form-control" required>
                                            <option value="">Select Type</option>
                                            <option value='percent'>Percent</option>
                                            <option value='amount'>Amount</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        }
                        <div class="form-group col-md-4 row">
                            <div class="form-group col-9">
                                <label for="invoice-number">Total<span className='required-label'>*</span></label>
                                <input value={perItemAmount} readOnly type="text" class="form-control" id="" />
                            </div>
                            <div class="form-group col-3 p-0 purchase-create-mid-plus-img">
                                <img className='img-fluid' onClick={() => handleSubmitItem()} style={{ cursor: 'pointer' }} src={plus} alt="" />
                            </div>
                        </div>
                    </div>

                    {allItems.length > 0 && <div className='my-table-responsive'>
                        <table class="table mt-4">
                            <thead class="thead-dark">
                                <tr>
                                    <th scope="col">Item</th>
                                    <th scope="col">Price</th>
                                    <th scope="col">Quantity</th>
                                    <th scope="col">GST</th>
                                    <th scope="col">Discount</th>
                                    <th scope="col">Total</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody className='table-borderless'>
                                {allItems.map((item, index) => {
                                    return (
                                        <tr>
                                            <td>
                                                {item.item.name}
                                            </td>
                                            <td>{item.sale_price}</td>
                                            <td>{item.qty}</td>
                                            <td>{item.gst.value}%</td>
                                            <td>{item.discountValue} {item.discount_type}</td>
                                            <td>{item.total}</td>
                                            <td>
                                                <i style={{ cursor: 'pointer' }} onClick={() => handleEdit(item, index+1)} class="fa fa-pencil" aria-hidden="true"></i>
                                                <i onClick={() => handleDelete(index)} style={{ cursor: "pointer" }} class="fa fa-trash" aria-hidden="true"></i>
                                            </td>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </table>
                    </div>}
                    <div className='row mt-5 justify-content-between purchase-create-footer'>
                        <div class="form-group col-md-5">
                            <label for="exampleFormControlTextarea1">Remarks</label>
                            <textarea value={remarks} onChange={e => setRemarks(e.target.value)} class="form-control" id="exampleFormControlTextarea1" rows="5"></textarea>
                            {currentCustomer ?<div>Due Amount: {currentCustomer.amount}</div> :null}
                        </div>
                        <div className='col-md-6'>
                            <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Total Value</label>
                                <div class="col-md-8">
                                    <input type="text" value={totalValue} class="form-control" id="" />
                                </div>
                            </div>
                            {discountType === 'Bill' && <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Discount</label>
                                <div class="pr-0 col-md-8 row">
                                    <div className="pr-0 col-5">
                                        <input class="form-control" value={finalDiscountValue} onChange={e => setFinalDiscountValue(e.target.value)} type="text" name="" id="" />
                                    </div>
                                    <div className="px-0 col-7">
                                        <select value={finalDiscountCriteria} onChange={e => setFinalDiscountCriteria(e.target.value)} class="form-control" required>
                                            <option value="">Select Type</option>
                                            <option value='percent'>Percent</option>
                                            <option value='amount'>Amount</option>
                                        </select>
                                    </div>
                                </div>
                            </div>}
                            {discountType === 'Both' && <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Discount</label>
                                <div class="pr-0 col-md-8 row">
                                    <div className="pr-0 col-5">
                                        <input class="form-control" type="text" value={finalDiscountValue} onChange={e => setFinalDiscountValue(e.target.value)} />
                                    </div>
                                    <div className="px-0 col-7">
                                        <select value={finalDiscountCriteria} onChange={e => setFinalDiscountCriteria(e.target.value)} class="form-control" required>
                                            <option value="">Select Type</option>
                                            <option value='percent'>Percent</option>
                                            <option value='amount'>Amount</option>
                                        </select>
                                    </div>
                                </div>
                            </div>}
                            <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Final Amount</label>
                                <div class="col-md-8">
                                    <input readOnly value={finalAmount} type="text" class="form-control" id="" />
                                </div>
                            </div>
                            <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Paid Amount</label>
                                <div class="col-md-8">
                                    <input value={paidAmount} onChange={e => setPaidAmount(e.target.value)} type="text" class="form-control" id="" />
                                </div>
                            </div>
                            {paidAmount > 0 && <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Payment Method</label>
                                <div class="col-md-8">
                                    <Select options={allPaymentMethods} value={currentPaymentMethod} onChange={setCurrentPaymentMethod} />
                                </div>
                            </div>}
                            <div class="form-group row">
                                <label for="" class="col-md-4 col-form-label">Remain Amount</label>
                                <div class="col-md-8">
                                    <input type="text" value={remainAmount} readOnly class="form-control" id="" />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="d-flex justify-content-center create-catagory-btns">
                    <button type="button" onClick={() => window.location.reload()} class="font-weight-bold m-3 py-2 px-4 btn btn-danger">Cancel<i
                        class="px-2 fa fa-times" aria-hidden="true"></i></button>
                    <button type="button" onClick={()=>finalSubmit()} class="font-weight-bold m-3 py-2 px-4 btn btn-success">Save<i
                        class="px-2 fa fa-floppy-o" aria-hidden="true"></i></button>
                </div>
            </form>
        </div>
    )
}
