import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { formatDate } from 'src/helpers/helpers';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from '../../components/CustomModal';
import InventoryAdjustModal from '../../components/InventoryAdjustModal';

export default function Index() {
    const { user, setLoad } = React.useContext(userContext);
    const [recieptList, setRecieptList] = React.useState([]);
    const [showModal, setShowModal] = React.useState(false);
    const [modal, setModal] = React.useState(false);
    const [type,setType] = React.useState('0');
    const [id, setId] = React.useState('');
    const [from_Date, setFromDate] = React.useState('');
    const [to_Date, setToDate] = React.useState('');
    const [total,setTotal] = React.useState(0);
    const [adjutAmount, setAdjustAmount] = React.useState('');

    const deleteEntry = () => {
        setLoad(true)
        async function deleteData() {
            const response = await fetch(url + 'deleteReciept/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            const data = await response.json();
            if (data.status === 200) {
                setModal(false);
                setId('');
                async function fetchData() {
                    const response = await fetch(url + 'fetchRecieptList', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })
                    if (response.ok === true) {
                        const data = await response.json();
                        setLoad(false)
                        if (data.status === 200) {
                            setRecieptList(data.list.map((item)=>{
                                return{
                                   'id': item.id,
                                   'payment_type': item.payment_type,
                                   'date': formatDate(item.date.split(' ')[0]),
                                   'type': item.type,
                                   'amount': item.amount,
                                   'condition': item.condition
                                }
                            }));
                            setTotal(parseInt(data.total_records));
                        } else {
                            toast.error(data.message);
                        }
                    }
                }
                fetchData();
            } else {
                setModal(false);
                setId('');
                setLoad(false);
                toast.error(data.message);
            }
        }
        deleteData();
    }
    const submitAdjust = () => {
        setLoad(true)
            setLoad(true)
            const formData = new FormData();
            formData.append('new_amount', adjutAmount);
            async function editData() {
                const respone = await fetch(url + 'updateReciept/' + id, {
                    method: 'POST',
                    headers: {
                        'Authorization': user.token,
                    },
                    body: formData
                })

                if (respone.ok === true) {
                    const data = await respone.json();
                    setLoad(false)
                    if (data.status === 200) {
                        setId('');
                        setAdjustAmount('');
                        setShowModal(false);
                        async function fetchData() {
                            const response = await fetch(url + 'fetchRecieptList', {
                                method: 'GET',
                                headers: {
                                    'Authorization': user?.token
                                }
                            })

                            if (response.ok === true) {
                                const data = await response.json();

                                if (data.status === 200) {
                                    setRecieptList(data.list.map((item)=>{
                                        return{
                                           'id': item.id,
                                           'payment_type': item.payment_type,
                                           'date': formatDate(item.date.split(' ')[0]),
                                           'type': item.type,
                                           'amount': item.amount,
                                           'condition': item.condition}
                                    }));
                                    setTotal(parseInt(data.total_records));
                                } else {
                                    toast.error(data.message);
                                }
                            }
                        }
                        fetchData();
                    } else {
                        toast.error(data.message)
                    }
                }
            }
            editData();
        }
    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'fetchRecieptList', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setRecieptList(data.list.map((item)=>{
                        return{
                           'id': item.id,
                           'payment_type': item.payment_type,
                           'date': formatDate(item.date.split(' ')[0]),
                           'type': item.type,
                           'amount': item.amount,
                           'condition': item.condition
                        }
                    }));
                    setTotal(parseInt(data.total_records));
                } else {
                    toast.error(data.message);
                }
            }
        }
        fetchData();
    }, [])

    function handleSubmit(){
        if(to_Date && from_Date){
            setLoad(true)
            async function submit(){
                const response = await fetch(url + `fetchRecieptList?to=${to_Date}&from=${from_Date}&type=${type}`,{
                    method : 'GET',
                    headers : {
                        'Authorization' : user?.token
                    }
                });

                if (response.ok === true){
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200){
                        setRecieptList(data.list.map((item)=>{
                            return{
                               'id': item.id,
                               'payment_type': item.payment_type,
                               'date': formatDate(item.date.split(' ')[0]),
                               'type': item.type,
                               'amount': item.amount,
                               'condition': item.condition
                            }
                        }));
                        setTotal(parseInt(data.total_records));
                    }else{
                        toast.error(data.message)
                    }
                }else{
                    toast.error('Oops something went wrong!')
                }
            }
            submit().catch(err=>toast.error('Internal server error!. Please try again later'))
        }else{
            toast.error('Please fill the to and from date')
        }
    }

    const columns = [
        {
            key: "id",
            text: "Reciept Id",
            className: "id",
            sortable: true,
        },
        {
            key: "type",
            text: "Type",
            className: "type",
            sortable: true
        },
        {
            key: "amount",
            text: "Amount",
            className: "amount",
            sortable: true
        },
        {
            key: "date",
            text: "Date",
            className: "date",
            sortable: true
        },
        {
            key: "condition",
            text: "Condition",
            className: "condition",
            sortable: true
        },
        {
            key: "payment_type",
            text: "Payment Type",
            className: "payment_type",
            sortable: true
        },
        {
            key: "action",
            text: "Action",
            className: "action",
            width: 100,
            sortable: false,
            cell: record => { 
                return (
                    <Fragment>
                        <i style={{ cursor: 'pointer' }} 
                           onClick={() => showModalFunc(record.id)} 
                           class="fa fa-pencil" 
                           aria-hidden="true">    
                        </i>
                        <i 
                        style={{ cursor: "pointer" }} 
                        onClick={() => showModalDel(record.id)} 
                        class="fa fa-trash" 
                        aria-hidden="true">   
                        </i>
                    </Fragment>
                );
            }
        }
    ];
    const config = {
        page_size: 10,
        length_menu: [ 10, 20, 50 ],
        button: {
            excel: false,
            print: false,
            extra: false,
        }
    }
    const showModalFunc = (id) => {
        setId(id);
        setShowModal(true);
    }
    const showModalDel = (id) => {
        setId(id);
        setModal(true);
    }
    async function fetchData(query) {
        setLoad(true);
        const response = await fetch(url + query, {
            method: 'GET',
            headers: {
                'Authorization': user?.token
            }
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                setRecieptList(data.list.map((item)=>{
                    return{
                       'id': item.id,
                       'payment_type': item.payment_type,
                       'date': formatDate(item.date.split(' ')[0]),
                       'type': item.type,
                       'amount': item.amount,
                       'condition': item.condition
                    }
                }));
                setTotal(parseInt(data.total_records));
            } else {
                toast.error(data.message);
            }
        }
    }
    const handleChange=(e)=>{
        let query=`fetchRecieptList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&to=${to_Date}&from=${from_Date}&type=${type}`;
        if(e.sort_order){
            let sort = JSON.stringify(e.sort_order);
            query=`fetchRecieptList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}&to=${to_Date}&from=${from_Date}&type=${type}`;
        }
        fetchData(query);
    }

    return (
        <section>
            <ToastContainer />
            <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
            <InventoryAdjustModal header="Adjust Reciept" Label="Amount" showModal={showModal} setShowModal={setShowModal} adjutAmount={adjutAmount} setAdjustAmount={setAdjustAmount} submitAdjust={submitAdjust}/>
            <form>
            <div class="form-row create-purchase-header">
            <div class="form-group col-md-3">
            <label for="date">From</label>
            <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date" required />
            </div>
            <div class="form-group col-md-3">
            <label for="date">To</label>
            <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date" required />
            </div>
            <div class="form-group col-md-3">
            <label for="input-type" class="col-md-4 col-form-label">Type</label>
            <select value={type} onChange={e => setType(e.target.value)} id="input-type" class="form-control" required>
            <option value="all">All</option>
            <option value="purchase">Purchase</option>
            <option value="sales">Sales</option>
            <option value="expense">Expenses</option>
            </select>
            </div>
            <div class="form-group col-md-3">
            <button type="Submit" class="font-weight-bold my-4 py-2 px-4 btn btn-success" onClick={ ()=> handleSubmit() }>Filter</button>
            </div>
            </div>
            </form>
            <ReactDatatable
                    config={config}
                    records={recieptList}
                    columns={columns}
                    total_record={total}
                    onChange={(e)=>handleChange(e)}
                    dynamic={true}
                />
        </section>
    )
}
