import { CChartDoughnut } from '@coreui/react-chartjs'
import React, { lazy } from 'react'
import ChartBarSimple from '../charts/ChartBarSimple.js'

const WidgetsDropdown = lazy(() => import('../widgets/WidgetsDropdown.js'))

const Dashboard = () => {
  return (
    <>
      <div className='row'>
        <div className='col-md-6'>
          <CChartDoughnut datasets={[
            {
              backgroundColor: [
                '#41B883',
                '#E46651',
                '#00D8FF',
              ],
              data: [ 20, 80, 10]
            }
          ]}
            labels={['Sales', 'Purchase', 'Expense']}
            options={{
              tooltips: {
                enabled: true
              }
            }} />
        </div>

        <div className='col-md-6'>
        <WidgetsDropdown />
        </div>
      </div>
      <ChartBarSimple style={{ height: "400px", marginTop: 40 }} label="Sales" />
      <ChartBarSimple style={{ height: "400px", marginTop: 40 }} label="Purchase" />
      <ChartBarSimple style={{ height: "400px", marginTop: 40 }} label="Expense" />

    </>
  )
}

export default Dashboard
