import React from 'react';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify';
import { userContext } from '../../context/UserContext';
import { formatDateTime } from '../../helpers/helpers';
import { useParams } from 'react-router-dom';

export default function DetailTransactionReport() {
    let { id } = useParams();
    const { user, setLoad } = React.useContext(userContext);
    const [from_Date, setFromDate] = React.useState(new Date());
    const [to_Date, setToDate] = React.useState(new Date());
    const [outAmount, setTotalOutAmount] = React.useState(0);
    const [inAmount, setTotalInAmount] = React.useState(0);
    const [transactionlist, setTransactionList] = React.useState([]);

    function handleSubmit(e) {
        e.preventDefault();
        if (to_Date && from_Date) {
            setLoad(true)
            const formData = new FormData();
            formData.append('id', id);
            formData.append('to', to_Date);
            formData.append('from', from_Date);
            async function submit() {
                const response = await fetch(url + 'detailTransactionReport', {
                    method: 'POST',
                    headers: {
                        'Authorization': user?.token
                    },
                    body: formData
                });

                if (response.ok === true) {
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200) {
                        setTotalOutAmount(parseFloat(data.out_amount).toFixed(2));
                        setTotalInAmount(parseFloat(data.in_amount).toFixed(2));
                        setTransactionList(data.transaction_list);
                    } else {
                        toast.error(data.message)
                    }
                } else {
                    toast.error('Oops something went wrong!')
                }
            }

            submit().catch(err => toast.error('Internal server error!. Please try again later'))
        } else {
            toast.error('Please fill the to and from date')
        }
    }

    function exportPDF() {
        const html = document.getElementById('export-table').innerHTML;
        var myWindow = window.open('', 'Receipt');
        myWindow.document.write('<html><head><title>Receipt</title>');
        myWindow.document.write('<link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.1/dist/css/bootstrap.min.css" integrity="sha384-zCbKRCUGaJDkqS1kPbPd7TveP5iyJE0EjAuZQTgFLD2ylzuqKfdKlfG/eSrtxUkn" crossorigin="anonymous">')
        myWindow.document.write("</head><body>");
        myWindow.document.write(html);
        myWindow.document.write('</body></html>');
        myWindow.document.close();

        myWindow.onload = function () {
            myWindow.focus();
            myWindow.print();
        }
    }
    return (
        <section>
            <ToastContainer />
            <div class="d-flex">
                <form class="col-md-10 m-0 p-0">
                    <div class="form-row m-0 p-0 create-purchase-header">
                        <div class="form-group col-md-3">
                            <label for="date">From<span className='required-label'>*</span></label>
                            <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date" required />
                        </div>
                        <div class="form-group col-md-3">
                            <label for="date">To<span className='required-label'>*</span></label>
                            <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date" required />
                        </div>
                        <div class="form-group col-md-2">
                            <button type="submit" class="font-weight-bold my-4 py-2 px-4 btn btn-success" onClick={(e) => handleSubmit(e)}>Submit</button>
                        </div>
                    </div>
                </form>
                <div class="col-md-2">
                    <button onClick={() => exportPDF()} class="font-weight-bold my-4 px-3 py-2 btn btn-success">Export as PDF</button>
                </div>
            </div>
            {/* table code */}
            <div id="export-table">
                {transactionlist.length > 0 && (
                    <>
                        <h6 className='text-center mt-4 font-weight-bold'>Transactions List</h6>
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Date</th>
                                    <th scope="col">Amount</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactionlist.map((item,index) => {
                                    return (
                                        <tr>
                                            <th scope="row">{++index}</th>
                                            <td>{formatDateTime(item.date_time)}</td>
                                            <td>{item.condition==="incoming" ? "+ " : "- "}{item.amount}</td>
                                        </tr>
                                    )
                                }
                                )}
                            </tbody>
                        </table>
                    </>
                )}
                <hr />

                <h6 className='float-left ml-2'>In : {parseFloat(inAmount).toFixed(2)}</h6>
                <h6 className='float-left ml-5'>Out : {parseFloat(outAmount).toFixed(2)}</h6>
                <h6 className='float-right mr-3'>Total Amount: {parseFloat(parseFloat(inAmount).toFixed(2)- parseFloat(outAmount).toFixed(2)).toFixed(2)} </h6>
            </div>
        </section >
    )
}
