import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import PayModal from '../../components/PayModal';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from 'src/components/CustomModal';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [modal, setModal] = React.useState(false);
  const [payAmount, setPayAmount] = React.useState(0);
  const [paymentMode, setPaymentMode] = React.useState('cash');
  const [paymodal,setPayModal] = React.useState(false);
  const { user,setLoad } = React.useContext(userContext);
  const [customerList, setCustomerList] = React.useState([]);
  const [total,setTotal] = React.useState(0); 

  const payBill = () => {
    setLoad(true)
    async function payBillServer(){
        const formData = new FormData();
        formData.append('pay_amount',payAmount);
        formData.append('payment_mode',paymentMode);
        const response = await fetch(url + 'pay/customer/' + id,{
            method : 'POST',
            headers : {
                'Authorization' : user.token
            },
            body : formData
        })

        if (response){
            const data = await response.json();

            if (data.status === 200){
                setPayModal(false);
                setId('');
                async function fetchData() {
                    const response = await fetch(url + 'fetchCustomerList', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })
                    if (response.ok === true) {
                        const data = await response.json();
                        setLoad(false)
                        if (data.status === 200) {
                          setCustomerList(data.list.map((item, index) => {
                            return {
                              '#': index + 1,
                              'id': item.id,
                              'name': item.name,
                              'email': item.email,
                              'mobile': item.mobile,
                              'gst_number': item.gst_number,
                              'due_amount': item.due_amount
                            }
                          }));
                          setTotal(parseInt(data.total_records));
                        } else {
                            toast.error(data.message);
                        }
                    }
                }
                fetchData();
            }else{
                toast.error(data.message);
            }
        }
    }
    payBillServer();
}

const payModal = (item,id) => {
  setPayAmount(parseFloat(item.due_amount))
  setPayModal(true);
  setId(id);
}

  React.useEffect(() => {
    setLoad(true)
    async function fetchData() {
      const response = await fetch(url + 'fetchCustomerList', {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })
      if(response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setCustomerList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'email': item.email,
              'mobile': item.mobile,
              'gst_number': item.gst_number,
              'due_amount': item.due_amount
            }
          }));
          setTotal(parseInt(data.total_records));
        }
      }
    }
    fetchData();
  }, [])

  const deleteEntry = () => {
    setLoad(true)
    async function deleteData() {
      const response = await fetch(url + 'deleteclient/' + id, {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })

      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {
        setModal(false);
        setId('');
        async function fetchData() {
          const response = await fetch(url + 'fetchCustomerList', {
            method: 'GET',
            headers: {
              'Authorization': user?.token
            }
          })

          if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
              setCustomerList(data.list.map((item, index) => {
                return {
                  '#': index + 1,
                  'id': item.id,
                  'name': item.name,
                  'email': item.email,
                  'mobile': item.mobile,
                  'gst_number': item.gst_number,
                  'due_amount': item.due_amount
                }
              }));
              setTotal(parseInt(data.total_records));
            }
          }
        }
        fetchData();

      } else {
        toast.error(data.message)
      }
    }
    deleteData();
  }

  const columns = [
    {
        key: "#",
        text: "#",
        className: "#",
        sortable: false,
    },
    {
        key: "name",
        text: "Customer Name",
        className: "name",
        sortable: true
    },
    {
        key: "email",
        text: "Email",
        className: "email",
        sortable: true
    },
    {
        key: "mobile",
        text: "Mobile",
        className: "mobile",
        sortable: true
    },
    {
        key: "gst_number",
        text: "GST No.",
        className: "gst_number",
        sortable: true
    },
    {
      key: "due_amount",
      text: "Due Amount",
      className: "due_amount",
      sortable: true
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        sortable: false,
        cell: record => { 
            return (
                <Fragment>
                    {parseFloat(record.due_amount) !== 0 && <i 
                      style={{cursor:'pointer'}} 
                      onClick={()=>payModal(record,record.id)} 
                      className="fa fa-money mr-2" 
                      aria-hidden="true">  
                      </i>
                    }
                    <Link
                        to={`/edit/customer/${record.id}`} 
                        style={{marginRight: '5px'}}>    
                        <i className="fa fa-pencil mr-1"></i>
                    </Link>
                    <i 
                    style={{ cursor: "pointer" }} 
                    onClick={() => showModal(record.id)} 
                    class="fa fa-trash" 
                    aria-hidden="true">   
                    </i>
                </Fragment>
            );
        }
    }
];

const config = {
    page_size: 10,
    length_menu: [ 10, 20, 50 ],
    button: {
        excel: false,
        print: false,
        extra: false,
    }
}
  const showModal = value => {
    setId(value);
    setModal(true)
  }
  async function fetchData(query) {
    setLoad(true);
    const response = await fetch(url + query, {
        method: 'GET',
        headers: {
            'Authorization': user?.token
        }
    })
    if (response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setCustomerList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'email': item.email,
              'mobile': item.mobile,
              'gst_number':item.gst_number,
              'due_amount': item.due_amount
            }
          }));
          setTotal(parseInt(data.total_records));
        } else {
            toast.error(data.message);
        }
    }
}
const handleChange=(e)=>{
  let query=`fetchCustomerList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}`;
  if(e.sort_order){
      let sort = JSON.stringify(e.sort_order);
      query=`fetchCustomerList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}`;
  }
  fetchData(query);
}

  return (
    <section>
      <ToastContainer />
      <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
      <PayModal paymodal={paymodal} setPayModal={setPayModal} payAmount={payAmount} setPayAmount={setPayAmount} paymentMode={paymentMode} payBill={payBill} setPaymentMode={setPaymentMode}  />
      <Link className='btn btn-primary float-right' to='/create/customer'>Create Customer</Link>
      <ReactDatatable
          config={config}
          records={customerList}
          columns={columns}
          total_record={total}
          onChange={(e)=>handleChange(e)}
          dynamic={true}
        />
    </section>
  )
}
