import React from 'react';
import Select from 'react-select';
import { url } from 'src/helpers/helpers';
import {userContext} from '../../context/UserContext'
import { toast,ToastContainer } from 'react-toastify';
import {useParams} from 'react-router-dom';

export default function Edit() {

    const {id} = useParams();
    const {user,setLoad} = React.useContext(userContext);
    const [name,setName] = React.useState('');
    const [allcategories, setAllCategories] = React.useState([]);
    const [currentCategory, setCurrentCategory] = React.useState('');
    const [type, setType] = React.useState('');
    const [unit, setUnit] = React.useState('');
    const [sales_unit, setSalesUnit] = React.useState(1);
    const [pack_unit, setPackUnit]  = React.useState(1);
    const [price,setPrice] = React.useState(0);
    const [hsbn,setHSBN] = React.useState('');
    const [serial_no,setSerialNo] = React.useState('');

    React.useEffect(()=>{
        async function fetchCategory(){
            setLoad(true);
            const response = await fetch(url + 'categorylist', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })
            if(response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setAllCategories(data?.category_list.map(item => {
                        return {
                            value: item.id,
                            label: item.name
                        }
                    }))
                } else {
                    toast.error(data.message)
                }
            }
        }
        fetchCategory();

        async function fetchProductData(){
                setLoad(true);
                const response = await fetch(url + 'productById/' + id,{
                method : 'GET',
                headers : {
                    'Authorization' : user?.token
                }
            })

            if (response.ok === true){
                setLoad(false);
                const data = await response.json();
                if (data.status === 200){
                    let product_detail = data.product_detail;
                    setName(product_detail.name);
                    setPrice(product_detail.price);
                    setCurrentCategory({
                        value: product_detail.category_id,
                        label: product_detail.category_name
                    });
                    setHSBN(product_detail.hsbn);
                    setSerialNo(product_detail.serial_no);
                    setType(product_detail.type);
                    setUnit(product_detail.unit);
                    setSalesUnit(product_detail.sales_unit);
                    setPackUnit(product_detail.pack_unit);
                }else{
                    toast.error(data.message);
                }
            }
        }
        fetchProductData();
    },[])

    const handleSubmit = e => {
        e.preventDefault();
        if(name && price && currentCategory && unit)
        {  setLoad(true);
        const formData = new FormData();
        formData.append('name',name);
        formData.append('price',price);
        formData.append('unit',unit);
        formData.append('type', type);
        formData.append('hsbn',hsbn);
        formData.append('serial_no',serial_no);
        formData.append('category_id',currentCategory.value);
        formData.append('category_name',currentCategory.label);
        formData.append('sales_unit',sales_unit);
        formData.append('pack_unit', pack_unit);

        async function submitData(){
            const response = await fetch(url + 'updateproduct/' + id,{
                method : 'POST',
                headers : {
                    'Authorization' : user?.token
                },
                body : formData
            })
            if (response.ok === true){
                setLoad(false);
                const data = await response.json();
                if (data.status === 200){
                    return window.location = window.location.origin + '/#/productList'
                }else{
                    toast.error(data.message);
                }
            }
        }
        submitData();
    }else{
        toast.error('Please fill the data with *');    
    }
    }
    return (
        <div className="container create-page-main-section">
            <ToastContainer />
            <form onSubmit={e=>handleSubmit(e)}>
                <div className='p-sm-5 create-form-field'>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Name:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={name} onChange={e=>setName(e.target.value)} type="text" class="form-control" id="inputPassword" required/>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Category:<span className='required-label'>*</span></label>    
                        <div class="col-sm-10">
                        <Select options={allcategories} value={currentCategory} onChange={setCurrentCategory} required/>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Type:<span className='required-label'>*</span></label>    
                        <div class="d-flex align-items-sm-center col-sm-10">
                        <select value={type} onChange={e => setType(e.target.value)} class="form-control" required>
                            <option value="">Select Type</option>
                            <option value='product'>Product</option>
                            <option value='service'>Service</option>
                        </select>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Unit:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                        <select value={unit} onChange={e => setUnit(e.target.value)} class="form-control" required>
                            <option value="">Select Unit</option>
                            <option value='Sq.Ft'>Sq.Ft</option>
                            <option value='Pcs'>Pcs</option>
                            <option value='Kg'>Kg</option>
                            <option value='Nos'>Nos</option>
                            <option value='Gms'>Gms</option>
                            <option value='Litre'>Litre</option>
                        </select>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Price:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={price} onChange={e=>setPrice(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">HSN:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={hsbn} onChange={e=>setHSBN(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Serial No:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={serial_no} onChange={e=>setSerialNo(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>                   
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Sales Unit:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={sales_unit} onChange={e=>setSalesUnit(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                    <label for="inputPassword" class="col-sm-2 col-form-label">Pack Unit:<span className='required-label'>*</span></label>
                    <div class="d-flex align-items-sm-center col-sm-10">
                        <input required value={pack_unit} onChange={e=>setPackUnit(e.target.value)} type="text" class="form-control" id="inputPassword" />
                    </div>
                    </div>
                </div>
                <div class="d-flex justify-content-center create-catagory-btns">
                    <button type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-danger">Cancel<i
                        class="px-2 fa fa-times" aria-hidden="true"></i></button>
                    <button type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-success">Save<i
                        class="px-2 fa fa-floppy-o" aria-hidden="true"></i></button>
                </div>
            </form>
        </div>
    )
}
