                                            <div class="col-5">
                                                <span class="text-120 text-secondary-d1">
                                                    {(parseFloat(saleHeader.total_after_roundoff) - parseFloat(saleHeader.taxable_amount) - parseFloat(saleHeader.item_discount_amount)).toFixed(2)}
                                                </span>
                                            </div>
                                        </div>

                                        <div class="row my-2">
                                            <div class="col-7 text-right">
                                                Tax
                                            </div>
                                            <div class="col-5">
                                                <span class="text-110 text-secondary-d1">
                                                    {saleHeader.taxable_amount}
                                                </span>
                                            </div>
                                        </div>

                                        <div class="row my-2">
                                            <div class="col-7 text-right">
                                                Discount
                                            </div>
                                            <div class="col-5">
                                                <span class="text-110 text-secondary-d1">
                                                    {saleHeader.item_discount_amount}
                                                </span>
                                            </div>
                                        </div>

                                        <div class="row my-2 align-items-center bgc-primary-l3 p-2">
                                            <div class="col-7 text-right">
                                                Total Amount
                                            </div>
                                            <div class="col-5">
                                                <span class="text-150 text-success-d3 opacity-2">
                                                    {saleHeader.total_after_roundoff}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <hr />

                                <div>
                                    <span class="text-secondary-d1 text-105">Thank you for your business</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* end print sales bill */}

        </section>
    )
}
