import React from 'react'
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import { url } from 'src/helpers/helpers';


export default function Create() {
    const { user,setLoad } = React.useContext(userContext);
    const [name, setName] = React.useState('');
    const [accno, setAccNo] = React.useState('');
    const [branch,setBranch] = React.useState('');
    const [ifsc, setIFSC] = React.useState('');
    const [total_amount, setTotalAmount] = React.useState(0);
    const handleSubmit = e => {
        setLoad(true)
        e.preventDefault();
        async function submitData() {
            const formData = new FormData();
            formData.append('accno',accno)
            formData.append('name', name);
            formData.append('ifsc',ifsc);
            formData.append('branch',branch);
            formData.append('total_amount', total_amount);

            const response = await fetch(url + 'createbank', {
                method: 'POST',
                headers: {
                    'Authorization': user?.token
                },
                body: formData
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    toast.success(data.message);
                    setName('');
                    setIFSC('');
                    setAccNo('');
                    setBranch('');
                    setTotalAmount(0);
                } else {
                    toast.error(data.message)
                }
            }
        }
        submitData();
    }

    return (
        <div className="container create-page-main-section">
            <ToastContainer />
            <form onSubmit={e => handleSubmit(e)}>
                <div className='p-sm-5 create-form-field'>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Bank No:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={accno} onChange={e => setAccNo(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Name:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={name} onChange={e => setName(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">IFSC:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={ifsc} onChange={e => setIFSC(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Branch:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={branch} onChange={e => setBranch(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Total Amount:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={total_amount} onChange={e => setTotalAmount(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                </div>
                <div class="d-flex justify-content-center create-catagory-btns">
                    <button onClick={() => window.location.reload()} type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-danger">Cancel<i
                        class="px-2 fa fa-times" aria-hidden="true"></i></button>
                    <button type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-success">Save<i
                        class="px-2 fa fa-floppy-o" aria-hidden="true"></i></button>
                </div>
            </form>
        </div>
    )
}
