import React from 'react'
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import { url } from 'src/helpers/helpers';
import { useParams } from 'react-router-dom'

export default function Edit() {
    const { id } = useParams();
    const { user,setLoad } = React.useContext(userContext);
    const [name, setName] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [mobile, setMobile] = React.useState('');
    const [address, setAddress] = React.useState('');
    const [gst, setGST] = React.useState('');
    const [due_amount, setDueAmount] = React.useState(0);

    const handleSubmit = e => {
        setLoad(true)
        e.preventDefault();

        async function submitData() {
            const formData = new FormData();
            formData.append('name', name);
            formData.append('email', email);
            formData.append('mobile', mobile);
            formData.append('address', address);
            formData.append('gst', gst);
            formData.append('due_amount', due_amount);

            const response = await fetch(url + 'updateparty/' + id, {
                method: 'POST',
                headers: {
                    'Authorization': user?.token
                },
                body: formData
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false);
                if (data.status === 200) {
                    return window.location = window.location.origin + '/#/partyList'
                } else {
                    toast.error(data.message)
                }
            }
        }
        submitData();
    }

    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'partyById/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                const party_detail = data.party_detail;
                setName(party_detail.name);
                setEmail(party_detail.email);
                setMobile(party_detail.mobile);
                setAddress(party_detail.address);
                setGST(party_detail.gst_number);
                setDueAmount(party_detail.due_amount);
            }
        }
        fetchData();
    }, [id])

    return (
        <div className="container create-page-main-section">
            <ToastContainer />
            <form onSubmit={e => handleSubmit(e)}>
                <div className='p-sm-5 create-form-field'>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Name:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={name} onChange={e => setName(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Email:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={email} onChange={e => setEmail(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Mobile No:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={mobile} onChange={e => setMobile(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">GST:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={gst} onChange={e => setGST(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Due Amount:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={due_amount} onChange={e => setDueAmount(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label class="col-sm-2 col-form-label" for="exampleFormControlTextarea1">Address:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <textarea value={address} onChange={e => setAddress(e.target.value)} class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                        </div>
                    </div>
                </div>
                <div class="d-flex justify-content-center create-catagory-btns">
                    <button onClick={() => window.location.reload()} type="button" class="font-weight-bold m-3 py-2 px-4 btn btn-danger">Cancel<i
                        class="px-2 fa fa-times" aria-hidden="true"></i></button>
                    <button type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-success">Save<i
                        class="px-2 fa fa-floppy-o" aria-hidden="true"></i></button>
                </div>
            </form>
        </div>
    )
}
