import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from '../../components/CustomModal';
import { formatDate } from 'src/helpers/helpers';
export default function SalesReport() {
    const { user,setLoad } = React.useContext(userContext);
    const [quotationList, setQuotationList] = React.useState([]);
    const [modal, setModal] = React.useState(false);
    const [from_Date, setFromDate] = React.useState('');
    const [to_Date, setToDate] = React.useState('');
    const [id, setId] = React.useState('');
    const [total,setTotal] = React.useState(0); 

    const deleteEntry = () => {
        setLoad(true)
        async function deleteData() {
            const response = await fetch(url + 'deleteQuotation/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            const data = await response.json();

            if (data.status === 200) {
                setModal(false);
                setId('');
                async function fetchData() {
                    const response = await fetch(url + 'fetchQuotationList', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })

                    if (response.ok === true) {
                        const data = await response.json();
                        setLoad(false)
                        if (data.status === 200) {
                            setQuotationList(data.list.map((item,index)=>{
                                return{
                                   '#': index+1,
                                   'id': item.id,
                                   'quotation_number': item.quotation_number,
                                   'customer_name': item.customer_name,
                                   'quotation_date': formatDate(item.quotation_date.split(' ')[0]),
                                   'total_after_roundoff': item.total_after_roundoff,
                                   'remain_amount': item.remain_amount
                                }
                            }));
                            setTotal(parseInt(data.total_records));
                        } else {
                            toast.error(data.message);
                        }
                    }
                }        
                fetchData();
            } else {
                toast.error(data.message)
            }
        }
        deleteData();
    }
    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'fetchQuotationList', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setQuotationList(data.list.map((item,index)=>{
                        return{
                           '#': index+1,
                           'id': item.id,
                           'quotation_number': item.quotation_number,
                           'customer_name': item.customer_name,
                           'quotation_date': formatDate(item.quotation_date.split(' ')[0]),
                           'total_after_roundoff': item.total_after_roundoff,
                           'remain_amount': item.remain_amount
                        }
                    }));
                    setTotal(parseInt(data.total_records));
                } else {
                    toast.error(data.message);
                }
            }
        }

        fetchData();
    }, [])

    const columns = [
        {
            key: "#",
            text: "#",
            className: "#",
            sortable: false,
        },
        {
            key: "quotation_number",
            text: "Quotation No",
            className: "quotation_number",
            sortable: true
        },
        {
            key: "quotation_date",
            text: "Quotation Date",
            className: "quotation_date",
            sortable: true
        },
        {
            key: "customer_name",
            text: "Customer Name",
            className: "customer_name",
            sortable: true
        },
        {
            key: "total_after_roundoff",
            text: "Bill Amount",
            className: "bill_amount",
            sortable: true
        },
        {
            key: "remain_amount",
            text: "Remain Amount",
            className: "remain_amount",
            sortable: true
        },
        {
            key: "action",
            text: "Action",
            className: "action",
            width: 100,
            sortable: false,
            cell: record => { 
                return (
                    <Fragment>
                        <Link
                            to={`/edit/quotation/${record.id}`} 
                            style={{marginRight: '5px'}}>    
                            <i className="fa fa-pencil mr-2"></i>
                        </Link>
                        <i 
                        style={{ cursor: "pointer" }} 
                        onClick={() => showModal(record.id)} 
                        class="fa fa-trash" 
                        aria-hidden="true">   
                        </i>
                    </Fragment>
                );
            }
        }
    ];
    const config = {
        page_size: 10,
        length_menu: [ 10, 20, 50 ],
        button: {
            excel: false,
            print: false,
            extra: false,
        }
    }

    const showModal = value => {
        setId(value);
        setModal(true)
    }

    async function fetchData(query) {
        setLoad(true);
        const response = await fetch(url + query, {
            method: 'GET',
            headers: {
                'Authorization': user?.token
            }
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                setQuotationList(data.list.map((item,index)=>{
                    return{
                       '#': index+1,
                       'id': item.id,
                       'quotation_number': item.quotation_number,
                       'customer_name': item.customer_name,
                       'quotation_date': formatDate(item.quotation_date.split(' ')[0]),
                       'total_after_roundoff': item.total_after_roundoff,
                       'remain_amount': item.remain_amount
                    }
                }));
                setTotal(parseInt(data.total_records));
            } else {
                toast.error(data.message);
            }
        }
    }

    function handleSubmit(e){
        e.preventDefault();
        if (to_Date && from_Date){
            setLoad(true);
            async function submit(){
                const response = await fetch(url + `fetchQuotationList?to=${to_Date}&from=${from_Date}`,{
                    method : 'GET',
                    headers : {
                        'Authorization' : user?.token
                    },
                });
                if (response.ok === true){
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200){
                        setQuotationList(data.list.map((item,index)=>{
                            return{
                               '#': index+1,
                               'id': item.id,
                               'quotation_number': item.quotation_number,
                               'customer_name': item.customer_name,
                               'quotation_date': formatDate(item.quotation_date.split(' ')[0]),
                               'total_after_roundoff': item.total_after_roundoff,
                               'remain_amount': item.remain_amount
                            }
                        }));
                        setTotal(parseInt(data.total_records));
                    }else{
                        toast.error(data.message)
                    }
                }else{
                    toast.error('Oops something went wrong!')
                }
            }
            submit().catch(err=>toast.error('Internal server error!. Please try again later'))
        }else{
            toast.error('Please fill the to and from date')
        }
    }
    const handleChange=(e)=>{
        let query=`fetchQuotationList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&to=${to_Date}&from=${from_Date}`;
        if(e.sort_order){
            let sort = JSON.stringify(e.sort_order);
            query=`fetchQuotationList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}&to=${to_Date}&from=${from_Date}`;
        }
        fetchData(query);
    }
    return (
        <section>
            <ToastContainer />
            <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
            <form onSubmit={(e)=>handleSubmit(e)}>
            <div class="form-row create-purchase-header">
            <div class="form-group col-md-3">
            <label for="date">From</label>
            <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date" required />
            </div>
            <div class="form-group col-md-3">
            <label for="date">To</label>
            <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date" required />
            </div>
            <div class="form-group col-md-3">
            <button type="Submit" class="font-weight-bold my-4 py-2 px-4 btn btn-success">Filter</button>
            </div>
            </div>
            </form>
            <ReactDatatable
                    config={config}
                    records={quotationList}
                    columns={columns}
                    total_record={total}
                    onChange={(e)=>handleChange(e)}
                    dynamic={true}
                />            
        </section>
    )
}
