import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from 'src/components/CustomModal';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [modal, setModal] = React.useState(false)
  const { user,setLoad } = React.useContext(userContext);
  const [productList, setProductList] = React.useState([]);
  const [total,setTotal] = React.useState(0); 

  React.useEffect(() => {
    setLoad(true)
    async function fetchData() {
      const response = await fetch(url + 'fetchProductList', {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })
      if(response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setProductList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'type': item.type,
              'hsbn': item.hsbn,
              'serial_no': item.serial_no,
              'category_name': item.category_name,
              'unit': item.unit,
              'sales_unit': item.sales_unit,
              'pack_unit': item.pack_unit,
              'price': item.price
            }
          }));
          setTotal(parseInt(data.total_records));
        }
      }
    }
    fetchData();
  }, [])

  const deleteEntry = () => {
    setLoad(true)
    async function deleteData() {
      const response = await fetch(url + 'deleteproduct/' + id, {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })

      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {
        setModal(false);
        setId('');
        async function fetchData() {
          const response = await fetch(url + 'fetchProductList', {
            method: 'GET',
            headers: {
              'Authorization': user?.token
            }
          })

          if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
              setProductList(data.list.map((item, index) => {
                return {
                  '#': index + 1,
                  'id': item.id,
                  'name': item.name,
                  'hsbn': item.hsbn,
                  'serial_no': item.serial_no,
                  'category_name': item.category_name,
                  'type': item.type,
                  'unit': item.unit,
                  'sales_unit': item.sales_unit,
                  'pack_unit': item.pack_unit,
                  'price': item.price
                }
              }));
              setTotal(parseInt(data.total_records));
            }
          }
        }
        fetchData();

      } else {
        toast.error(data.message)
      }
    }
    deleteData();
  }

  const columns = [
    {
        key: "#",
        text: "#",
        className: "#",
        sortable: false,
    },
    {
      key: "serial_no",
      text: "Serial No",
      className: "serial_no",
      sortable: true
    },
    {
        key: "name",
        text: "Product Name",
        className: "name",
        sortable: true
    },
    {
        key: "category_name",
        text: "Category",
        className: "category_name",
        sortable: true
    },
    {
      key: "type",
      text: "Type",
      className: "type",
      sortable: true
    },
    {
        key: "unit",
        text: "Unit",
        className: "unit",
        sortable: true
    },
    {
      key: "pack_unit",
      text: "Pack Unit",
      className: "pack_unit",
      sortable: true
    },
    {
      key: "sales_unit",
      text: "Sales Unit",
      className: "sales_unit",
      sortable: true
    },
    {
      key: "hsbn",
      text: "HSBN Value",
      className: "hsbn",
      sortable: true
    },
    {
      key: "price",
      text: "Price",
      className: "price",
      sortable: true
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        sortable: false,
        cell: record => { 
            return (
                <Fragment>
                    <Link
                        to={`/edit/product/${record.id}`} 
                        style={{marginRight: '5px'}}>    
                        <i className="fa fa-pencil mr-1"></i>
                    </Link>
                    <i 
                    style={{ cursor: "pointer" }} 
                    onClick={() => showModal(record.id)} 
                    class="fa fa-trash" 
                    aria-hidden="true">   
                    </i>
                </Fragment>
            );
        }
    }
];

const config = {
    page_size: 10,
    length_menu: [ 10, 20, 50 ],
    button: {
        excel: false,
        print: false,
        extra: false,
    }
}
  const showModal = value => {
    setId(value);
    setModal(true)
  }
  async function fetchData(query) {
    setLoad(true);
    const response = await fetch(url + query, {
        method: 'GET',
        headers: {
            'Authorization': user?.token
        }
    })
    if (response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
          setProductList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'serial_no': item.serial_no,
              'hsbn': item.hsbn,
              'type': item.type,
              'category_name': item.category_name,
              'unit': item.unit,
              'sales_unit': item.sales_unit,
              'pack_unit': item.pack_unit,
              'price': item.price
            }
          }));
          setTotal(parseInt(data.total_records));
        } else {
            toast.error(data.message);
        }
    }
}
const handleChange=(e)=>{
  let query=`fetchProductList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}`;
  if(e.sort_order){
      let sort = JSON.stringify(e.sort_order);
      query=`fetchProductList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}`;
  }
  fetchData(query);
}

  return (
    <section>
      <ToastContainer />
      <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
      <Link className='btn btn-primary float-right' to='/create/product'>Create Product</Link>
      <ReactDatatable
          config={config}
          records={productList}
          columns={columns}
          total_record={total}
          onChange={(e)=>handleChange(e)}
          dynamic={true}
        />
    </section>
  )
}
