import React, { Fragment } from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from '../../components/CustomModal';
import { formatDate } from 'src/helpers/helpers';
import PayModal from '../../components/PayModal';

export default function SalesReport() {
    const { user, setLoad } = React.useContext(userContext);
    const [salesList, setSalesList] = React.useState([]);
    const [modal, setModal] = React.useState(false);
    const [payAmount, setPayAmount] = React.useState(0);
    const [paymentMode, setPaymentMode] = React.useState({
        label: "cash"
    });
    const [paymodal, setPayModal] = React.useState(false);
    const [from_Date, setFromDate] = React.useState('');
    const [to_Date, setToDate] = React.useState('');
    const [id, setId] = React.useState('');
    const [total, setTotal] = React.useState(0);

    const payBill = () => {
        setLoad(true)
        async function payBillServer() {
            const formData = new FormData();
            formData.append('pay_amount', payAmount);
            formData.append('payment_mode', paymentMode.label);
            const response = await fetch(url + 'pay/sales/' + id, {
                method: 'POST',
                headers: {
                    'Authorization': user.token
                },
                body: formData
            })

            if (response) {
                const data = await response.json();

                if (data.status === 200) {
                    setPayModal(false);
                    setId('');
                    async function fetchData() {
                        const response = await fetch(url + 'fetchSalesList', {
                            method: 'GET',
                            headers: {
                                'Authorization': user?.token
                            }
                        })
                        if (response.ok === true) {
                            const data = await response.json();
                            setLoad(false)
                            if (data.status === 200) {
                                setSalesList(data.list.map((item, index) => {
                                    return {
                                        '#': index + 1,
                                        'id': item.id,
                                        'invoice_number': item.invoice_number,
                                        'customer_name': item.customer_name,
                                        'warehouse_name': item.warehouse_name,
                                        'sale_date': formatDate(item.sale_date.split(' ')[0]),
                                        'total_after_roundoff': item.total_after_roundoff,
                                        'remain_amount': item.remain_amount
                                    }
                                }));
                                setTotal(parseInt(data.total_records));
                            } else {
                                toast.error(data.message);
                            }
                        }
                    }
                    fetchData();
                } else {
                    toast.error(data.message);
                }
            }
        }
        payBillServer();
    }

    const payModal = (item, id) => {
        setPayAmount(parseFloat(item.remain_amount))
        setPayModal(true);
        setId(id);
    }

    const deleteEntry = () => {
        setLoad(true)
        async function deleteData() {
            const response = await fetch(url + 'deleteSale/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            const data = await response.json();

            if (data.status === 200) {
                setModal(false);
                setId('');
                async function fetchData() {
                    const response = await fetch(url + 'fetchSalesList', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })

                    if (response.ok === true) {
                        const data = await response.json();
                        setLoad(false)
                        if (data.status === 200) {
                            setSalesList(data.list.map((item, index) => {
                                return {
                                    '#': index + 1,
                                    'id': item.id,
                                    'customer_name': item.customer_name,
                                    'warehouse_name': item.warehouse_name,
                                    'sale_date': formatDate(item.sale_date.split(' ')[0]),
                                    'discount_amount': parseFloat(item.bill_discount_percentage) ? `${item.bill_discount_percentage}%` : item.bill_discount_amount,
                                    'total_after_roundoff': item.total_after_roundoff,
                                    'amount_paid': item.amount_paid,
                                    'remain_amount': item.remain_amount
                                }
                            }));
                            setTotal(parseInt(data.total_records));
                        } else {
                            toast.error(data.message);
                        }
                    }
                }
                fetchData();
            } else {
                toast.error(data.message)
            }
        }
        deleteData();
    }
    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'fetchSalesList', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setSalesList(data.list.map((item, index) => {
                        return {
                            '#': index + 1,
                            'id': item.id,
                            'invoice_number': item.invoice_number,
                            'customer_name': item.customer_name,
                            'warehouse_name': item.warehouse_name,
                            'sale_date': formatDate(item.sale_date.split(' ')[0]),
                            'total_after_roundoff': item.total_after_roundoff,
                            'remain_amount': item.remain_amount
                        }
                    }));
                    setTotal(parseInt(data.total_records));
                } else {
                    toast.error(data.message);
                }
            }
        }

        fetchData();
    }, [])

    function printBillBig(id) {
        async function fetchDetailSales() {
            setLoad(true);
            const response = await fetch(url + 'getSaleById/' + id, {
                headers: {
                    'Authorization': user.token
                }
            });

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    console.log(data)
                } else {
                    toast.error(data.message);
                }
            }
        }
        fetchDetailSales()
    }

    const columns = [
        {
            key: "#",
            text: "#",
            className: "#",
            sortable: false,
        },
        {
            key: "invoice_number",
            text: "Invoice Number",
            className: "invoice_number",
            sortable: true
        },
        {
            key: "warehouse_name",
            text: "Warehouse Name",
            className: "warehouse_name",
            sortable: true
        },
        {
            key: "customer_name",
            text: "Customer Name",
            className: "customer_name",
            sortable: true
        },
        {
            key: "sale_date",
            text: "Sale Date",
            className: "sale_date",
            sortable: true
        },
        {
            key: "total_after_roundoff",
            text: "Bill Amount",
            className: "bill_amount",
            sortable: true
        },
        {
            key: "remain_amount",
            text: "Remain Amount",
            className: "remain_amount",
            sortable: true
        },
        {
            key: "action",
            text: "Action",
            className: "action",
            width: 100,
            sortable: false,
            cell: record => {
                return (
                    <Fragment>
                        {parseFloat(record.remain_amount) !== 0 && <i
                            style={{ cursor: 'pointer' }}
                            onClick={() => payModal(record, record.id)}
                            className="fa fa-money mr-2"
                            aria-hidden="true">
                        </i>
                        }
                        <Link
                            to={`/edit/sales/${record.id}`}
                            style={{ marginRight: '5px' }}>
                            <i className="fa fa-pencil mr-2"></i>
                        </Link>
                        <i
                            style={{ cursor: "pointer" }}
                            onClick={() => showModal(record.id)}
                            class="fa fa-trash"
                            aria-hidden="true">
                        </i>
                        <i onClick={() => printBillBig(record.id)} style={{ cursor: 'pointer' }} className='ml-1 fa fa-print'></i>
                    </Fragment>
                );
            }
        }
    ];
    const config = {
        page_size: 10,
        length_menu: [10, 20, 50],
        button: {
            excel: false,
            print: false,
            extra: false,
        }
    }

    const showModal = value => {
        setId(value);
        setModal(true)
    }

    async function fetchData(query) {
        setLoad(true);
        const response = await fetch(url + query, {
            method: 'GET',
            headers: {
                'Authorization': user?.token
            }
        })
        if (response.ok === true) {
            const data = await response.json();
            setLoad(false)
            if (data.status === 200) {
                setSalesList(data.list.map((item, index) => {
                    return {
                        '#': index + 1,
                        'id': item.id,
                        'invoice_number': item.invoice_number,
                        'customer_name': item.customer_name,
                        'warehouse_name': item.warehouse_name,
                        'sale_date': formatDate(item.sale_date.split(' ')[0]),
                        'total_after_roundoff': item.total_after_roundoff,
                        'remain_amount': item.remain_amount
                    }
                }));
                setTotal(parseInt(data.total_records));
            } else {
                toast.error(data.message);
            }
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (to_Date && from_Date) {
            setLoad(true);
            async function submit() {
                const response = await fetch(url + `fetchSalesList?to=${to_Date}&from=${from_Date}`, {
                    method: 'GET',
                    headers: {
                        'Authorization': user?.token
                    },
                });
                if (response.ok === true) {
                    const data = await response.json();
                    setLoad(false)
                    if (data.status === 200) {
                        setSalesList(data.list.map((item, index) => {
                            return {
                                '#': index + 1,
                                'id': item.id,
                                'invoice_number': item.invoice_number,
                                'customer_name': item.customer_name,
                                'warehouse_name': item.warehouse_name,
                                'sale_date': formatDate(item.sale_date.split(' ')[0]),
                                'total_after_roundoff': item.total_after_roundoff,
                                'remain_amount': item.remain_amount
                            }
                        }));
                        setTotal(parseInt(data.total_records));
                    } else {
                        toast.error(data.message)
                    }
                } else {
                    toast.error('Oops something went wrong!')
                }
            }
            submit().catch(err => toast.error('Internal server error!. Please try again later'))
        } else {
            toast.error('Please fill the to and from date')
        }
    }
    const handleChange = (e) => {
        let query = `fetchSalesList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&to=${to_Date}&from=${from_Date}`;
        if (e.sort_order) {
            let sort = JSON.stringify(e.sort_order);
            query = `fetchSalesList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}&to=${to_Date}&from=${from_Date}`;
        }
        fetchData(query);
    }
    return (
        <section>
            <ToastContainer />
            <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
            <PayModal paymodal={paymodal} setPayModal={setPayModal} payAmount={payAmount} setPayAmount={setPayAmount} paymentMode={paymentMode} payBill={payBill} setPaymentMode={setPaymentMode} />
            <form onSubmit={(e) => handleSubmit(e)}>
                <div class="form-row create-purchase-header">
                    <div class="form-group col-md-3">
                        <label for="date">From</label>
                        <input value={from_Date} onChange={e => setFromDate(e.target.value)} type="date" class="form-control" id="date" required />
                    </div>
                    <div class="form-group col-md-3">
                        <label for="date">To</label>
                        <input value={to_Date} onChange={e => setToDate(e.target.value)} type="date" class="form-control" id="date" required />
                    </div>
                    <div class="form-group col-md-3">
                        <button type="Submit" class="font-weight-bold my-4 py-2 px-4 btn btn-success">Filter</button>
                    </div>
                </div>
            </form>
            <ReactDatatable
                config={config}
                records={salesList}
                columns={columns}
                total_record={total}
                onChange={(e) => handleChange(e)}
                dynamic={true}
            />

        </section>
    )
}
