import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import Select from 'react-select';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import InventoryAdjustModal from 'src/components/InventoryAdjustModal';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [showModal, setShowModal] = React.useState(false)
  const { user,setLoad } = React.useContext(userContext);
  const [inventoryList, setInventoryList] = React.useState([]);
  const [adjustAmount,setAdjustAmount] = React.useState(0);
  const [total,setTotal] = React.useState(0); 
  const [allWarehouses, setAllWarehouses] = React.useState([]);
  const [currentWarehouse, setCurrentWarehouse] = React.useState('');
  const [allProducts,setAllProducts] = React.useState([]);
  const [currentProduct, setCurrentProduct] = React.useState('');

const showModalFunc = (id) => {
    setId(id);
    setShowModal(true);
}

const submitAdjust = () => {
    setLoad(true)
    const formData = new FormData();
    formData.append('new_qty',adjustAmount);
    async function editData(){

        const respone = await fetch(url + 'updateInventory/' + id,{
            method : 'POST',
            headers : {
                'Authorization' : user.token,
            },
            body : formData
        })

        if (respone.ok === true){
            const data = await respone.json();
            setLoad(false)
            if (data.status === 200){
                setId('');
                setAdjustAmount('');
                setShowModal(false);
                async function fetchData() {
                    const response = await fetch(url + 'fetchInventoryReport', {
                        method: 'GET',
                        headers: {
                            'Authorization': user?.token
                        }
                    })

                    if (response.ok === true) {
                        const data = await response.json();

                        if (data.status === 200) {
                            setInventoryList(data.list.map((item, index) => {
                                return {
                                  '#': index + 1,
                                  'id': item.id,
                                  'product_name': item.product_name,
                                  'warehouse_name': item.warehouse_name,
                                  'total_qty': item.total_qty,
                                }
                              }));
                              setTotal(parseInt(data.total_records));
                        } else {
                            toast.error(data.message);
                        }
                    }
                }
                fetchData();
            }else{
                toast.error(data.message)
            }
        }
    }
    editData();
}

function handleSubmit(){
   let string="";
    if(currentWarehouse && currentProduct){
       string=`product=${currentProduct.label}&warehouse=${currentWarehouse.label}`;
    }
    else if(currentWarehouse){
       string=`warehouse=${currentWarehouse.label}`; 
    }
    else if(currentProduct){
        string=`product=${currentProduct.label}`;
    }    
      setLoad(true)
      async function submit(){
          const response = await fetch(url + `fetchInventoryReport?${string}`,{
              method : 'GET',
              headers : {
                  'Authorization' : user?.token
              }
          });

          if (response.ok === true){
              const data = await response.json();
              setLoad(false)
              if (data.status === 200){
                setInventoryList(data.list.map((item, index) => {
                  return {
                    '#': index + 1,
                    'id': item.id,
                    'product_name': item.product_name,
                    'warehouse_name': item.warehouse_name,
                    'total_qty': item.total_qty,
                  }
                }));
                setTotal(parseInt(data.total_records));
              }else{
                  toast.error(data.message)
              }
          }else{
              toast.error('Oops something went wrong!')
          }
      }
      submit().catch(err=>toast.error('Internal server error!. Please try again later'))
 }
  React.useEffect(() => {
    setLoad(true)

    async function fetchWarehouse() {
      setLoad(true);
      const response = await fetch(url + 'warehouselist', {
          method: 'GET',
          headers: {
              'Authorization': user?.token
          }
      })

      if (response.ok === true) {
          const data = await response.json();
          setLoad(false)
          if (data.status === 200) {
              setAllWarehouses(data?.warehouse_list.map(item => {
