import React from 'react';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'

export default function DayWiseProfit() {
    const { user,setLoad } = React.useContext(userContext);
    const [expense, setExpense] = React.useState(0);
    const [sale, setSale] = React.useState(0);
    const [purchase, setPurchase] = React.useState(0);
    const [sale_return, setSaleReturn] = React.useState(0);
    const [purchase_return, setPurchaseReturn] = React.useState(0);
    const [customer_in,setCustomerIn] = React.useState(0);
    const [party_out,setPartyOut] = React.useState(0);
    const [to, setTo] = React.useState('');
    const [from, setFrom] = React.useState('');

    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'get-total-profile-record', {
                method: 'GET',
                headers: {
                    'Authorization': user?.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    setExpense(data.expense_record);
                    setPurchase(data.purchase_record);
                    setSale(data.sales_record);
                    setSaleReturn(data.sales_return_record);
                    setPurchaseReturn(data.purchase_return_record);
                    setCustomerIn(data.customer_record);
                    setPartyOut(data.party_record);
                } else {
                    toast.error(data.message);
                }
            }
        }

        fetchData();
    }, [])

    function handleSubmit(e){
        e.preventDefault();
        if (to && from) {
            setLoad(true)
            const formData = new FormData();
            formData.append('to', to);
            formData.append('from', from);
            async function submit() {
                const response = await fetch(url + 'get-profit-data', {
                    method: 'POST',
                    headers: {
                        'Authorization': user?.token
                    },
                    body: formData
                });

                if (response.ok === true) {
                    const data = await response.json();
                    setLoad(false)
                    if (data.status == 200) {
                        setExpense(data.expense_record);
                        setPurchase(data.purchase_record);
                        setSale(data.sales_record);
                        setSaleReturn(data.sales_return_record);
                        setPurchaseReturn(data.purchase_return_record);
                        setCustomerIn(data.customer_record);
                        setPartyOut(data.party_record);
                    } else {
                        toast.error(data.message)
                    }
                } else {
                    toast.error('Oops something went wrong!')
                }
            }

            submit().catch(err => toast.error('Internal server error!. Please try again later'))
        } else {
            toast.error('Please fill the to and from date')
        }
    }

    return (
        <section>
                <ToastContainer />
                    <form onSubmit={(e)=>handleSubmit(e)}>
                    <div class="form-row create-purchase-header">
                    <div className="form-group col-md-3">
                        <label htmlFor="">From</label>
                        <input type="date" value={from} onChange={e => setFrom(e.target.value)} className="form-control" required/>
                    </div>
                    <div className="form-group col-md-3">
                        <label htmlFor="">To</label>
                        <input type="date" value={to} onChange={e => setTo(e.target.value)} className="form-control" required/>
                    </div>
                    <div className="form-group col-md-3">
                        <button type="submit" className="font-weight-bold my-4 py-2 px-4 btn btn-primary">Submit</button>
                    </div>
                    </div>
                    </form>            
            <section className='day-wise-profit-table col-md-4'>
                <table className='bg-white w-100 text-dark px-3 py-3'>
                    <tr>
                        <td>Sales</td>
                        <td> {sale}</td>
                    </tr>
                    <tr>
                        <td>Purchase</td>
                        <td>- {purchase}</td>
                    </tr>
                    <tr>
                        <td>Expense</td>
                        <td>- {expense}</td>
                    </tr>
                    <tr>
                        <td>Purchase Return</td>
                        <td>+ {purchase_return}</td>
                    </tr>
                    <tr>
                        <td>Sale Return</td>
                        <td>- {sale_return}</td>
                    </tr>
                    <tr>
                        <td>Customer In</td>
                        <td>+ {customer_in}</td>
                    </tr>
                    <tr>
                        <td>Party Out</td>
                        <td>- {party_out}</td>
                    </tr>
                    <tr>
                        <td style={{fontWeight:'bold'}}>Total</td>
                        <td style={{fontWeight:'bold'}}>{parseFloat(sale) - parseFloat(purchase) - parseFloat(expense) + parseFloat(purchase_return) - parseFloat(sale_return) + parseFloat(customer_in) - parseFloat(party_out)}</td>
                    </tr>
                </table>
            </section>
        </section>
    )
}
