import React,{Fragment} from 'react';
import ReactDatatable from '@ashvin27/react-datatable';
import { Link } from 'react-router-dom';
import { url } from 'src/helpers/helpers';
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import CustomModal from 'src/components/CustomModal';

export default function Index() {
  const [id, setId] = React.useState(null);
  const [modal, setModal] = React.useState(false);
  const { user,setLoad } = React.useContext(userContext);
  const [bankList, setBankList] = React.useState([]);
  const [total,setTotal] = React.useState(0); 

  React.useEffect(() => {
    setLoad(true)
    async function fetchData() {
      const response = await fetch(url + 'fetchBankList', {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })
      if(response.ok === true) {
        const data = await response.json();
        setLoad(false);
        if (data.status === 200) {
          setBankList(data.list.map((item, index) => {
            return {
              '#': index + 1,
              'id': item.id,
              'name': item.name,
              'branch': item.branch,
              'accno': item.accno,
              'total_amount': item.total_amount,
              'ifsc':item.ifsc
            }
          }));
          setTotal(parseInt(data.total_records));
        }
      }
    }
    fetchData();
  }, [])

  const deleteEntry = () => {
    setLoad(true)
    async function deleteData() {
      const response = await fetch(url + 'deletebank/' + id, {
        method: 'GET',
        headers: {
          'Authorization': user?.token
        }
      })

      const data = await response.json();
      setLoad(false)
      if (data.status === 200) {
        setModal(false);
        setId('');
        async function fetchData() {
          const response = await fetch(url + 'fetchBankList', {
            method: 'GET',
            headers: {
              'Authorization': user?.token
            }
          })

          if (response.ok === true) {
            const data = await response.json();
            if (data.status === 200) {
                setBankList(data.list.map((item, index) => {
                    return {
                      '#': index + 1,
                      'id': item.id,
                      'name': item.name,
                      'accno': item.accno,
                      'branch': item.branch,
                      'total_amount': item.total_amount,
                      'ifsc':item.ifsc
                    }
                  }));
                  setTotal(parseInt(data.total_records));
            }
          }
        }
        fetchData();

      } else {
        toast.error(data.message)
      }
    }
    deleteData();
  }

  const columns = [
    {
        key: "#",
        text: "#",
        className: "#",
        sortable: false,
    },
    {
        key: "accno",
        text: "Bank No",
        className: "accno",
        sortable: true
    },
    {
        key: "name",
        text: "Bank Name",
        className: "name",
        sortable: true
    },
    {
        key: "branch",
        text: "Branch",
        className: "branch",
        sortable: true
    },
    {
        key: "ifsc",
        text: "IFSC",
        className: "ifsc",
        sortable: true
    },
    {
      key: "total_amount",
      text: "Total Amount",
      className: "total_amount",
      sortable: true
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        sortable: false,
        cell: record => { 
            return (
                <Fragment>
                    <Link
                        to={`/edit/bank/${record.id}`} 
                        style={{marginRight: '3px'}}>    
                        <i className="fa fa-pencil mr-1"></i>
                    </Link>
                    <i 
                    style={{ cursor: "pointer" }} 
                    onClick={() => showModal(record.id)} 
                    class="fa fa-trash mr-2" 
                    aria-hidden="true">   
                    </i>
                    <Link
                        to={`/DetailTransactionReport/${record.id}`} 
                        style={{cursor : "pointer"}}>    
                        <i className="fa fa-list-alt"></i>
                    </Link>
                </Fragment>
            );
        }
    }
];

const config = {
    page_size: 10,
    length_menu: [ 10, 20, 50 ],
    button: {
        excel: false,
        print: false,
        extra: false,
    }
}
  const showModal = value => {
    setId(value);
    setModal(true)
  }
  async function fetchData(query) {
    setLoad(true);
    const response = await fetch(url + query, {
        method: 'GET',
        headers: {
            'Authorization': user?.token
        }
    })
    if (response.ok === true) {
        const data = await response.json();
        setLoad(false)
        if (data.status === 200) {
            setBankList(data.list.map((item, index) => {
                return {
                  '#': index + 1,
                  'id': item.id,
                  'name': item.name,
                  'accno': item.accno,
                  'branch': item.branch,
                  'total_amount': item.total_amount,
                  'ifsc':item.ifsc
                }
              }));
              setTotal(parseInt(data.total_records));
        } else {
            toast.error(data.message);
        }
    }
}
const handleChange=(e)=>{
  let query=`fetchBankList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}`;
  if(e.sort_order){
      let sort = JSON.stringify(e.sort_order);
      query=`fetchBankList?page_number=${e.page_number}&page_size=${e.page_size}&filter_value=${e.filter_value}&sort_order=${sort}`;
  }
  fetchData(query);
}

  return (
    <section>
      <ToastContainer />
      <CustomModal modal={modal} setModal={setModal} deleteEntry={deleteEntry} />
      <Link className='btn btn-primary float-right' to='/create/bank'>Create Bank</Link>
      <ReactDatatable
          config={config}
          records={bankList}
          columns={columns}
          total_record={total}
          onChange={(e)=>handleChange(e)}
          dynamic={true}
        />
    </section>
  )
}
