import { CCard, CCardBody, CDataTable } from '@coreui/react';
import React from 'react'
import { Link } from 'react-router-dom';

export default function Index() {
  const fields = ['#', 'name', 'code', 'action'];
  const [list, setList] = React.useState([]);

  function showModal(id){

  }
  return (
    <section>
      <Link to="/create-admin" className='btn btn-primary mb-3 align-right'>
        Create Admin
      </Link>
      <CCard>
        <CCardBody>
          <CDataTable
            items={list}
            fields={fields}
            columnFilter
            tableFilter
            itemsPerPageSelect
            itemsPerPage={5}
            hover
            sorter
            pagination
            scopedSlots={{
              'action': (item) => (
                <td>
                  <Link to={`/edit/category/${item.id}`}><i class="fa fa-pencil" aria-hidden="true"></i></Link>
                  <i style={{ cursor: "pointer" }} onClick={() => showModal(item.id)} class="fa fa-trash" aria-hidden="true"></i>
                </td>
              ),

              '#': (item, index) => (
                <td>
                  {index + 1}
                </td>
              )
            }
            }
          />
        </CCardBody>
      </CCard>
    </section>
  )
}
