import React from 'react'
import { toast, ToastContainer } from 'react-toastify'
import { userContext } from '../../context/UserContext'
import { url } from 'src/helpers/helpers';
import { useParams } from 'react-router-dom'

export default function Edit() {
    const { id } = useParams();
    const { user,setLoad } = React.useContext(userContext);
    const [name, setName] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [status, setStatus] = React.useState('');
    const [mobile, setMobile] = React.useState('');
    const [address, setAddress] = React.useState('');
    const [expiry_date, setExpiryDate] = React.useState('');
    const [print_pref_purchase, setPrintPrefPurchase] = React.useState('');
    const [print_pref_sales, setPrintPrefSales] = React.useState('');
    const [gst, setGST] = React.useState('');
    const [non_gst_name, setNonGSTName] = React.useState('');
    const [default_gst, setDefaultGST] = React.useState(false);
    const [first_amount, setFirstAmount] = React.useState(0);
    const [renew_amount, setRenewAmount] = React.useState(0);

    const handleSubmit = e => {
        setLoad(true)
        e.preventDefault();
        async function submitData() {
            const formData = new FormData();
            formData.append('name', name);
            formData.append('email', email);
            formData.append('password', password);
            formData.append('mobile', mobile);
            formData.append('address', address);
            formData.append('gst', gst);
            formData.append('status', status);
            formData.append('expiry_date',expiry_date);
            formData.append('print_pref_purchase', print_pref_purchase);
            formData.append('print_pref_sales', print_pref_sales);
            formData.append('first_amount', first_amount);
            formData.append('renew_amount', renew_amount);
            formData.append('non_gst_name', non_gst_name);
            formData.append('default_gst', default_gst);

            const response = await fetch(url + 'updatecompany/' + id, {
                method: 'POST',
                headers: {
                    'Authorization': user?.token
                },
                body: formData
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                if (data.status === 200) {
                    return window.location = window.location.origin + '/#/companyList'
                } else {
                    toast.error(data.message)
                }
            }
        }
        submitData();
    }

    React.useEffect(() => {
        setLoad(true)
        async function fetchData() {
            const response = await fetch(url + 'companyById/' + id, {
                method: 'GET',
                headers: {
                    'Authorization': user.token
                }
            })

            if (response.ok === true) {
                const data = await response.json();
                setLoad(false)
                const company_detail = data.company_detail;
                setName(company_detail.name);
                setEmail(company_detail.email);
                setMobile(company_detail.mobile);
                setPassword(company_detail.password);
                setAddress(company_detail.address);
                setGST(company_detail.gst);
                setExpiryDate(company_detail.expiry_date);
                setStatus(company_detail.status);
                setPrintPrefPurchase(company_detail.print_preference_purchase);
                setPrintPrefSales(company_detail.print_preference_sales);
                setFirstAmount(company_detail.one_time_amount);
                setRenewAmount(company_detail.renew_amount);
                setNonGSTName(company_detail.non_gst_name);
                setDefaultGST(company_detail.default_gst);
            }
        }
        fetchData();
    }, [id])

    return (
        <div className="container create-page-main-section">
            <ToastContainer />
            <form onSubmit={e => handleSubmit(e)}>
                <div className='p-sm-5 create-form-field'>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Name:<span className='required-label'>*</span></label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input required value={name} onChange={e => setName(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Email:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={email} onChange={e => setEmail(e.target.value)} type="email" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Password:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={password} onChange={e => setPassword(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Mobile No:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={mobile} onChange={e => setMobile(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">GST:</label>
                        <div class="d-flex b-0 col-sm-1">
                        <input checked={default_gst} type="checkbox" onChange={e => setDefaultGST(!default_gst)} id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">GST No:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={gst} onChange={e => setGST(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Non GST Name:-</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={non_gst_name} onChange={e => setNonGSTName(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Print Preference(Purchase):</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                        <select value={print_pref_purchase} onChange={e => setPrintPrefPurchase(e.target.value)} class="form-control" required>
                            <option value="">Select Preference</option>
                            <option value="Thermal">Thermal</option>
                            <option value="A4">A4</option>
                        </select>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Print Preference(Sales):</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                        <select value={print_pref_sales} onChange={e => setPrintPrefSales(e.target.value)} class="form-control" required>
                            <option value="">Select Preference</option>
                            <option value="Thermal">Thermal</option>
                            <option value="A4">A4</option>
                        </select>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">First Amount:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={first_amount} onChange={e => setFirstAmount(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Renew Amount</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={renew_amount} onChange={e => setRenewAmount(e.target.value)} type="text" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Expiry Date:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <input value={expiry_date} onChange={e => setExpiryDate(e.target.value)} type="date" class="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputPassword" class="col-sm-2 col-form-label">Status:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                        <select value={status} onChange={e => setStatus(e.target.value)} class="form-control" required>
                            <option value="">Select Status</option>
                            <option value="1">Active</option>
                            <option value="2">InActive</option>
                        </select>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label class="col-sm-2 col-form-label" for="exampleFormControlTextarea1">Address:</label>
                        <div class="d-flex align-items-sm-center col-sm-10">
                            <textarea value={address} onChange={e => setAddress(e.target.value)} class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                        </div>
                    </div>
                </div>
                <div class="d-flex justify-content-center create-catagory-btns">
                    <button onClick={() => window.location.reload()} type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-danger">Cancel<i
                        class="px-2 fa fa-times" aria-hidden="true"></i></button>
                    <button type="submit" class="font-weight-bold m-3 py-2 px-4 btn btn-success">Save<i
                        class="px-2 fa fa-floppy-o" aria-hidden="true"></i></button>
                </div>
            </form>
        </div>
    )
}
